`;

const LoadButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  flex-direction: column;
`;

const LoadButton = styled.button`
  padding: 20px;
  border: 2px solid white;
  background: transparent;
  color: white;
  font-size: 24px;
  cursor: pointer;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 30px;
  &:hover {
    background-color: grey;
    border-color: grey;
    cursor: pointer;
    img {
      filter: brightness(0.7);
    }
  }
`;

const LogoOnButton = styled.img`
  height: 100px;
  width: 100px;
`;

const LoadButtonText = styled.div`
  position: absolute;
  top: -40px;
  font-size: 20px;
  color: white;
`;

const LoadingText = styled.div`
  margin-top: 20px;
  font-size: 20px;
  color: white;
`;

export const SOTD = () => {
  const [data, setData] = useState({});
  const [name, setName] = useState('');
  const [isVisible, setIsVisible] = useState(true);
  const [isVisible2, setIsVisible2] = useState(false);
  const [isVisible3, setIsVisible3] = useState(false);
  const [contentLoaded, setContentLoaded] = useState(false);
  const [loadingStep, setLoadingStep] = useState(0);

  useEffect(() => {
    axios
      .get('http://localhost:5002/api/stock_data')  // Update the port here
      .then((response) => {
        console.log('SUCCESS', response);
        setData(response.data);
        setContentLoaded(true);
      })
      .catch((error) => {
        console.log('ERROR:', error);
      });

    axios
      .get('http://localhost:5002/api/name')  // Update the port here
      .then((response) => {
        console.log('SUCCESS', response);
        setName(response.data.name);
      })
      .catch((error) => {
        console.log('ERROR:', error);
      });
  }, []);


  const handleClick = (tab) => {
    if (tab === 'day-trading') {
      setIsVisible(true);
      setIsVisible2(false);
      setIsVisible3(false);
    } else if (tab === 'swing-trading') {
      setIsVisible(false);
      setIsVisible2(true);
      setIsVisible3(false);
    } else if (tab === 'long-term') {
      setIsVisible(false);
      setIsVisible2(false);
      setIsVisible3(true);
    }
  };

  const loadContent = () => {
    setLoadingStep(1);
    setTimeout(() => {
      setLoadingStep(2);
      setTimeout(() => {
        setContentLoaded(true);
      }, 500);
    }, 500);
  };

  return (
    <PageContainer>
      {!contentLoaded ? (
        <LoadButtonContainer>
          <LoadButton onClick={loadContent}>
            <LoadButtonText>Tap to view</LoadButtonText>
            <LogoOnButton src={Logo} />
          </LoadButton>
          {loadingStep === 0 && <LoadingText>Stock of the day</LoadingText>}
          {loadingStep === 1 && <LoadingText>Initializing real-time data...</LoadingText>}
          {loadingStep === 2 && <LoadingText>Calculating short-float...</LoadingText>}
        </LoadButtonContainer>
      ) : (
        <div>
          <ContactTextContainer>
            <ContactText selected={isVisible} onClick={() => handleClick('day-trading')}>
              <div>Day-trading</div>
            </ContactText>
            <ContactText selected={isVisible2} onClick={() => handleClick('swing-trading')}>
              <div>Swing-trading</div>
            </ContactText>
            <ContactText selected={isVisible3} onClick={() => handleClick('long-term')}>
              <div>Long-term</div>
            </ContactText>
          </ContactTextContainer>
          <ContactText4>
            <div>Top picks for today</div>
          </ContactText4>
          <p>{name}</p>
          {isVisible && (
            <Container>
              {data['Day Trading']?.map((stock, index) => (
                <Item key={index}>
                  <ItemRow>
                    <TickerTextFirst>{stock.ticker}</TickerTextFirst>
                    <PriceChangeContainer>
                      <Price>${stock.price}</Price>
                    </PriceChangeContainer>
                  </ItemRow>
                  <ItemRow>
                    <Subtitle>{stock.name}</Subtitle>
                    <PriceChangeContainer>
                      <Change>{stock.change_percent}%</Change>
                    </PriceChangeContainer>
                  </ItemRow>
                  <Line />
                  <DetailsContainer>
                    <InfoContainer>
                      <VolumeText>
                        <TextLabel>VOLUME:</TextLabel> {stock.volume}
                      </VolumeText>
                      <HighText>
                        <TextLabel>HIGH:</TextLabel> {stock.Upper_BB}
                      </HighText>
                      <LowText>
                        <TextLabel>LOW:</TextLabel> {stock.Lower_BB}
                      </LowText>
                    </InfoContainer>
                    <LogoContainer>
                      <LogoImage src={Logo} />
                    </LogoContainer>
                  </DetailsContainer>
                  <NewsHeader>{stock.ticker} NEWS</NewsHeader>
                </Item>
              ))}
            </Container>
          )}
          {isVisible2 && (
            <Container>
              {data['Swing Trading']?.map((stock, index) => (
                <Item key={index}>
                  <TickerTextFirst>{stock.ticker}</TickerTextFirst>
                  <Subtitle>{stock.name}</Subtitle>
                  <Line />
                </Item>
              ))}
            </Container>
          )}
          {isVisible3 && (
            <Container>
              {data['Long Term']?.map((stock, index) => (
                <Item key={index}>
                  <TickerTextFirst>{stock.ticker}</TickerTextFirst>
                  <Subtitle>{stock.name}</Subtitle>
                  <Line />
                </Item>
              ))}
            </Container>
          )}
        </div>
      )}
    </PageContainer>
  );
};
