import React from "react";
import styled, { keyframes } from 'styled-components';
import WideBull from '../../WideBull.jpg';
import { useState, useEffect } from "react";

// Animation for fading in content
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

// Animation for scaling on hover
const hoverScale = keyframes`
  from {
    transform: scale(1);
  }
  to {
    transform: scale(1.05);
  }
`;

// Animation for floating ghost
const float = keyframes`
  50% {
     transform: translate(0, 20px);
  }
`;

// Animation for shrinking shadow
const shrink = keyframes`
  0% {
    width: 90%;
    margin: 0 5%;
  }
  50% {
    width: 60%;
    margin: 0 18%;
  }
  100% {
    width: 90%;
    margin: 0 5%;
  }
`;

const PageContainer = styled.div`
  background: linear-gradient(135deg, #0d0d0d 0%, #434343 100%);
  min-height: 220vh;
  padding-bottom: 50px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: row;
`;

const GhostContainer = styled.div`
  position: relative;
  width: 200px;
  height: 230px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;

  .ghost {
    animation: ${float} 3s ease-out infinite;
  }

  .shadowFrame {
    width: 200px;
    margin-top: 15px;
  }

  .shadow {
    animation: ${shrink} 3s ease-out infinite;
    transform-origin: center center;
  }
`;

const AboutText1 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-start;
  align-items: center;
  flex-direction: column;
  font-size: 75px;
  font-family: 'Poppins', sans-serif;
  font-weight: bold;
  margin-bottom: 70px;
  margin-top: 120px;
  padding: 10px;
  animation: ${fadeIn} 1s ease-in-out;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: white;
`;

const AboutText2 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-start;
  align-items: center;
  flex-direction: column;
  font-size: 32px;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 20px;
  color: #b3b3b3;
  animation: ${fadeIn} 1.5s ease-in-out;
`;

const AboutText3 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-start;
  align-items: center;
  flex-direction: column;
  font-size: 22px;
  font-family: 'Poppins', sans-serif;
  width: 66%;
  margin: auto;
  color: #D4F1F4;
  margin-bottom: 60px;
  animation: ${fadeIn} 2s ease-in-out;
  line-height: 1.6;
`;

const AboutText4 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-start;
  align-items: center;
  flex-direction: row;
  font-size: 32px;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 30px;
  color: #FFF;
  margin-top: 140px;
  animation: ${fadeIn} 2.5s ease-in-out;
`;

const AboutText8 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-start;
  align-items: center;
  flex-direction: row;
  font-size: 22px;
  font-family: 'Poppins', sans-serif;
  width: 66%;
  margin: auto;
  color: #D4F1F4;
  margin-bottom: 50px;
  animation: ${fadeIn} 3s ease-in-out;
  line-height: 1.6;
`;

const AboutTextContainer = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  flex-direction: row;
  font-family: 'Poppins', sans-serif;
  margin: 35px 0;
  margin-top: 30px;
  animation: ${fadeIn} 3.5s ease-in-out;
`;

const AboutTextContainer2 = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  flex-direction: row;
  font-family: 'Poppins', sans-serif;
  margin: 35px 0;
  animation: ${fadeIn} 4s ease-in-out;
`;

const AboutText5 = styled.div`
  font-size: 28px;
  border: 2px solid #D4F1F4;
  width: 15%; 
  height: 10%; 
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 40px;
  padding: 10px;
  color: ${props => (props.selected ? '#189AB4' : '#FFF')};
  background-color: ${props => (props.selected ? '#D4F1F4' : 'transparent')};
  cursor: pointer;
  transition: all 0.3s ease-out;
  position: relative;
  z-index: 1;

  &:hover {
    animation: ${hoverScale} 0.3s forwards;
    color: #189AB4;
    background-color: #FFF;
    box-shadow: 0px 0px 15px 2px rgba(255, 255, 255, 0.2);
  }

  @media (max-width: 768px) {
    font-size: 24px;
  }
  @media (max-width: 576px) {
    font-size: 20px;
  }
`;

const AboutText6 = styled(AboutText5)`
`;

const AboutText7 = styled(AboutText5)`
`;

const NewDiv = styled.div`
  margin-top: 30px;
  font-size: 20px;
  width: 1000px;
  display: ${props => props.show ? 'flex' : 'none'};
  color: #FFF;
  text-align: left;
  padding: 10px 0;
  align-items: center;
  justify-content: center;
  flex-direction: row;
  flex-wrap: wrap;
  animation: ${fadeIn} 4.5s ease-in-out;

  @media (max-width: 768px) {
    font-size: 18px;
    width: 90%;
  }

  @media (max-width: 480px) {
    font-size: 16px;
    width: 100%;
  }
`;

const NewDiv1 = styled(NewDiv)`
`;

const NewDiv2 = styled(NewDiv)`
`;

const WideBullimg = styled.img`
  width: 65%;
  height: auto; /* Maintain aspect ratio */
  position: relative;
  margin: 60px auto 0;
  display: block;
  border-radius: 15px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.5);
  transition: transform 0.3s ease-out;
  animation: ${fadeIn} 5s ease-in-out;

  &:hover {
    transform: scale(1.05);
  }

  @media (max-width: 768px) {
    width: 90%;
    height: auto; /* Maintain aspect ratio */
  }

  @media (max-width: 480px) {
    width: 100%;
    height: auto; /* Maintain aspect ratio */
  }
`;

export const About = () => {
  const [showDiv, setShowDiv] = useState(true);
  const [showDiv1, setShowDiv1] = useState(false);
  const [showDiv2, setShowDiv2] = useState(false);
  const [selectedTab, setSelectedTab] = useState(0);

  useEffect(() => {
    localStorage.setItem("selectedTab", selectedTab);
  }, [selectedTab]);

  const handleTabClick = (index) => {
    setShowDiv(index === 0);
    setShowDiv1(index === 1);
    setShowDiv2(index === 2);
    setSelectedTab(index);
  };

  return (
    <PageContainer>
      <GhostContainer>
        <svg className="ghost" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink" width="127.433px" height="132.743px" viewBox="0 0 127.433 132.743" enableBackground="new 0 0 127.433 132.743" xmlSpace="preserve">
