import styled, { keyframes } from 'styled-components';
import Bull from '../../BullOnCliff.PNG';
import React, { useState } from "react";

// Keyframes for the bull running animation
const runAcrossScreen = keyframes`
  0% {
    transform: translateX(-100%) scale(0.5);
    opacity: 0;
  }
  10% {
    transform: translateX(-90%) scale(0.6);
    opacity: 1;
  }
  50% {
    transform: translateX(50%) scale(1);
    opacity: 1;
  }
  70% {
    transform: translateX(70%) scale(1.2);
    opacity: 1;
  }
  100% {
    transform: translateX(200%) scale(1.5);
    opacity: 0;
  }
`;

// Background animation
const backgroundShift = keyframes`
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 1000px 0;
  }
`;

const HomeWrapper1 = styled.div`
  background: linear-gradient(0deg, #111, #333);
  background-size: 200% 100%;
  animation: ${backgroundShift} 20s linear infinite;
  width: 100%;
  height: 200vh;
  position: absolute;
  top: 0;

  @media (max-width: 768px) {
    height: 250vh;
  }
`;

const Bullimg = styled.img`
  width: 20%;
  height: auto;
  position: absolute;
  bottom: 20%;
  animation: ${runAcrossScreen} 5s infinite cubic-bezier(0.7, -0.5, 0.3, 1.5);

  @media (max-width: 1024px) {
    width: 30%;
    bottom: 15%;
  }

  @media (max-width: 768px) {
    width: 50%;
    bottom: 10%;
  }
`;

const HomeText1 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 5vw;
  font-family: Ubuntu;
  margin: 125px 0;
  font-weight: bold;
  color: #D4F1F4;
  margin-top: 200px;
  z-index: 1;

  @media (max-width: 768px) {
    font-size: 8vw;
    margin-top: 100px;
  }
`;

const HomeText2 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 2vw;
  font-family: Ubuntu;
  margin: -115px 0;
  color: white;

  @media (max-width: 768px) {
    font-size: 4vw;
  }
`;

export const Home = () => {
  const [showDisclaimer, setShowDisclaimer] = useState(!sessionStorage.getItem("disclaimerShown"));

  const closeDisclaimer = () => {
    setShowDisclaimer(false);
    sessionStorage.setItem("disclaimerShown", "true");
  };

  return (
    <div>
      <HomeWrapper1>
        <HomeText1>
          <div> Get the best, Earn the most </div>
        </HomeText1>
        <HomeText2>
          <div> Utilizing AI to generate </div>
          <div> top-tier stocks daily </div>
        </HomeText2>
        <Bullimg src={Bull} />
      </HomeWrapper1>
    </div>
  );
};
