import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled, { css } from 'styled-components';
import Logo from '../../WhiteLogo.PNG';

const PageContainer = styled.div`
  background-color: #4c4c4c;
  min-height: 101vh;
`;

const ContactTextContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  font-family: Ubuntu;
  margin: 85px 0;
`;

const ContactText4 = styled.div`
  font-size: 80px;
  text-align: center;
  margin-top: 4cm;
  font-weight: bold;
  color: #D4F1F4;
`;

const ContactText = styled.div`
  font-size: 28px;
  border: 2px solid #D4F1F4;
  width: 19%;
  height: 14%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 40px;
  padding: 10px 10px;
  color: white;
  box-sizing: border-box;
  flex-direction: row;
  background-color: ${props => (props.selected ? 'white' : 'transparent')};
  color: ${props => (props.selected ? 'white' : 'white')};
  &:hover {
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #D4F1F4;
    color: #4c4c4c;
    cursor: pointer;
    transition: background-color 0.3s ease-out;
    flex-direction: row;
  }
  ${({ selected }) =>
    selected &&
    css`
      &:after {
        width: 100%;
      }
      background-color: navy;
      color: white;
    `}
`;

const LoadButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  flex-direction: column;
`;

const LoadButton = styled.button`
  padding: 20px;
  border: 2px solid white;
  background: transparent;
  color: white;
  font-size: 24px;
  cursor: pointer;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 30px;
  &:hover {
    background-color: grey;
    border-color: grey;
    cursor: pointer;
    img {
      filter: brightness(0.7);
    }
  }
`;

const LogoOnButton = styled.img`
  height: 100px;
  width: 100px;
`;

const LoadButtonText = styled.div`
  position: absolute;
  top: -40px;
  font-size: 20px;
  color: white;
`;

const LoadingText = styled.div`
  margin-top: 20px;
  font-size: 20px;
  color: white;
`;

export const SOTD = () => {
  const [filteredStocks, setFilteredStocks] = useState({});
  const [isVisible, setIsVisible] = useState(true);
  const [isVisible2, setIsVisible2] = useState(false);
  const [isVisible3, setIsVisible3] = useState(false);
  const [contentLoaded, setContentLoaded] = useState(false);
  const [loadingStep, setLoadingStep] = useState(0);

  useEffect(() => {
    axios
      .get('http://localhost:5001/api/filtered_stocks')
      .then((response) => {
        console.log('SUCCESS', response);
        setFilteredStocks(response.data);
      })
      .catch((error) => {
        console.log('ERROR:', error);
      });
  }, []);

  const handleClick = (tab) => {
    if (tab === 'day-trading') {
      setIsVisible(true);
      setIsVisible2(false);
      setIsVisible3(false);
    } else if (tab === 'swing-trading') {
      setIsVisible(false);
      setIsVisible2(true);
      setIsVisible3(false);
    } else if (tab === 'long-term') {
      setIsVisible(false);
      setIsVisible2(false);
      setIsVisible3(true);
    }
  };

  const loadContent = () => {
    setLoadingStep(1);
    setTimeout(() => {
      setLoadingStep(2);
      setTimeout(() => {
        setContentLoaded(true);
      }, 500);
    }, 500);
  };

  return (
    <PageContainer>
      {!contentLoaded ? (
        <LoadButtonContainer>
          <LoadButton onClick={loadContent}>
            <LoadButtonText>Tap to view</LoadButtonText>
            <LogoOnButton src={Logo} />
          </LoadButton>
          {loadingStep === 0 && <LoadingText>Stock of the day</LoadingText>}
          {loadingStep === 1 && <LoadingText>Initializing real-time data...</LoadingText>}
          {loadingStep === 2 && <LoadingText>Calculating short-float...</LoadingText>}
        </LoadButtonContainer>
      ) : (
        <div>
          <ContactTextContainer>
            <ContactText selected={isVisible} onClick={() => handleClick('day-trading')}>
              <div>Day-trading</div>
            </ContactText>
            <ContactText selected={isVisible2} onClick={() => handleClick('swing-trading')}>
              <div>Swing-trading</div>
            </ContactText>
            <ContactText selected={isVisible3} onClick={() => handleClick('long-term')}>
              <div>Long-term</div>
            </ContactText>
          </ContactTextContainer>
          <ContactText4>
            <div>Top picks for today</div>
          </ContactText4>
          {isVisible && (
            <div>
              <h2>Day Trading Stocks</h2>
              <ul>
                {filteredStocks['Day Trading']?.map((stock, index) => (
                  <li key={index}>{stock.ticker}</li>
                ))}
              </ul>
            </div>
          )}
          {isVisible2 && (
            <div>
              <h2>Swing Trading Stocks</h2>
              <ul>
                {filteredStocks['Swing Trading']?.map((stock, index) => (
                  <li key={index}>{stock.ticker}</li>
                ))}
              </ul>
            </div>
          )}
          {isVisible3 && (
            <div>
              <h2>Long Term Stocks</h2>
              <ul>
                {filteredStocks['Long Term']?.map((stock, index) => (
                  <li key={index}>{stock.ticker}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </PageContainer>
  );
};
