export const SOTD = () => {
  const [data, setData] = useState({});
  const [isVisible, setIsVisible] = useState(true);
  const [isVisible2, setIsVisible2] = useState(false);
  const [isVisible3, setIsVisible3] = useState(false);
  const [contentLoaded, setContentLoaded] = useState(false);
  const [loadingStep, setLoadingStep] = useState(0);

  useEffect(() => {
    axios
      .get('http://your-chatgpt-api-endpoint', {
        params: {
          query: 'stocks of the day',
        },
      })
      .then((response) => {
        console.log('SUCCESS', response);
        if (response.data.resultStatus !== 'FAILURE') {
          setData(response.data); // Assuming your API returns the data in a compatible format
          setContentLoaded(true);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleClick = (tab) => {
    if (tab === 'day-trading') {
      setIsVisible(true);
      setIsVisible2(false);
      setIsVisible3(false);
    } else if (tab === 'swing-trading') {
      setIsVisible(false);
      setIsVisible2(true);
      setIsVisible3(false);
    } else if (tab === 'long-term') {
      setIsVisible(false);
      setIsVisible2(false);
      setIsVisible3(true);
    }
  };

  const loadContent = () => {
    setLoadingStep(1);
    setTimeout(() => {
      setLoadingStep(2);
      setTimeout(() => {
        setContentLoaded(true);
      }, 500);
    }, 500);
  };

  return (
    <PageContainer>
      {!contentLoaded ? (
        <LoadButtonContainer>
          <LoadButton onClick={loadContent}>
            <LoadButtonText>Tap to view</LoadButtonText>
            <LogoOnButton src={Logo} />
          </LoadButton>
          {loadingStep === 0 && <LoadingText>Stock of the day</LoadingText>}
          {loadingStep === 1 && <LoadingText>Initializing real-time data...</LoadingText>}
          {loadingStep === 2 && <LoadingText>Calculating short-float...</LoadingText>}
        </LoadButtonContainer>
      ) : (
        <div>
          <br />
          <br />
          <br />
          <br />
          <br />
          <ContactTextContainer>
            <ContactText $selected={isVisible} onClick={() => handleClick('day-trading')}>
              <div>Day-trading</div>
            </ContactText>
            <ContactText $selected={isVisible2} onClick={() => handleClick('swing-trading')}>
              <div>Swing-trading</div>
            </ContactText>
            <ContactText $selected={isVisible3} onClick={() => handleClick('long-term')}>
              <div>Long-term</div>
            </ContactText>
          </ContactTextContainer>
          <ContactText4>
            <div>Top picks for 5/8</div>
          </ContactText4>
          {isVisible && (
            <Container>
              {data?.daytrading?.ticker?.map((ticker, index) => (
                <Item key={index}>
                  <ItemRow>
                    <TickerTextFirst>{ticker}</TickerTextFirst>
                    <PriceChangeContainer>
                      <Price>${data.daytrading['stock info'][index][0]}</Price>
                    </PriceChangeContainer>
                  </ItemRow>
                  <ItemRow>
                    <Subtitle>{data?.daytrading?.company?.[index]}</Subtitle>
                    <PriceChangeContainer>
                      <Change>{data.daytrading['stock info'][index][1]} {data.daytrading['stock info'][index][2]}</Change>
                    </PriceChangeContainer>
                  </ItemRow>
                  <Line />
                  <DetailsContainer>
                    <InfoContainer>
                      <VolumeText>
                        <TextLabel>VOLUME:</TextLabel> {data.daytrading['stock info'][index][3]}
                      </VolumeText>
                      <HighText>
                        <TextLabel>HIGH:</TextLabel> {data.daytrading['stock info'][index][4]}
                      </HighText>
                      <LowText>
                        <TextLabel>LOW:</TextLabel> {data.daytrading['stock info'][index][5]}
                      </LowText>
                    </InfoContainer>
                    <LogoContainer>
                      <LogoImage src={Logo} />
                    </LogoContainer>
                  </DetailsContainer>
                  <NewsHeader>{ticker} NEWS</NewsHeader>
                </Item>
              ))}
            </Container>
          )}

          {isVisible2 && (
            <Container>
              {data?.swingtrading?.ticker?.map((ticker, index) => (
                <Item key={index}>
                  <TickerTextFirst>{ticker}</TickerTextFirst>
                  <Subtitle>{data?.swingtrading?.company?.[index]}</Subtitle>
                  <Line />
