import React, { useState } from "react";
import styled, { keyframes } from "styled-components";
import WideBull from "../../WideBull.jpg";

// Animation for fading in content
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

// Animation for scaling on hover
const hoverScale = keyframes`
  from {
    transform: scale(1);
  }
  to {
    transform: scale(1.05);
  }
`;

const PageContainer = styled.div`
  background: linear-gradient(135deg, #0d0d0d 0%, #434343 100%);
  min-height: 220vh;
  padding-bottom: 50px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const AboutText1 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 75px;
  font-family: 'Poppins', sans-serif;
  font-weight: bold;
  margin-bottom: 70px;
  margin-top: 120px;
  padding: 10px;
  animation: ${fadeIn} 1s ease-in-out;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: white;
`;

const AboutText2 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 32px;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 20px;
  color: #b3b3b3;
  animation: ${fadeIn} 1.5s ease-in-out;
`;

const AboutText3 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 22px;
  font-family: 'Poppins', sans-serif;
  width: 66%;
  margin: auto;
  color: #D4F1F4;
  margin-bottom: 60px;
  animation: ${fadeIn} 2s ease-in-out;
  line-height: 1.6;
`;

const AboutText4 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  font-size: 32px;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 30px;
  color: #FFF;
  margin-top: 140px;
  animation: ${fadeIn} 2.5s ease-in-out;
`;

const AboutText8 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  font-size: 22px;
  font-family: 'Poppins', sans-serif;
  width: 66%;
  margin: auto;
  color: #D4F1F4;
  margin-bottom: 50px;
  animation: ${fadeIn} 3s ease-in-out;
  line-height: 1.6;
`;

const AboutTextContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  font-family: 'Poppins', sans-serif;
  margin: 35px 0;
  margin-top: 30px;
  animation: ${fadeIn} 3.5s ease-in-out;
`;

const AboutTextContainer2 = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  font-family: 'Poppins', sans-serif;
  margin: 35px 0;
  animation: ${fadeIn} 4s ease-in-out;
`;

const AboutText5 = styled.div`
  font-size: 22px;
  border: 2px solid #D4F1F4;
  width: 20%;
  height: 15%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
  padding: 15px;
  color: ${(props) => (props.selected ? '#189AB4' : '#FFF')};
  background-color: ${(props) =>
    props.selected ? '#D4F1F4' : 'transparent'};
  cursor: pointer;
  transition: all 0.3s ease-out;
  position: relative;
  z-index: 1;

  &:hover {
    animation: ${hoverScale} 0.3s forwards;
    color: #189AB4;
    background-color: #FFF;
    box-shadow: 0px 0px 15px 2px rgba(255, 255, 255, 0.2);
  }

  @media (max-width: 768px) {
    font-size: 18px;
  }
  @media (max-width: 576px) {
    font-size: 16px;
  }
`;

const WideBullimg = styled.img`
  width: 65%;
  height: auto;
  position: relative;
  margin: 60px auto 0;
  display: block;
  border-radius: 15px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.5);
  transition: transform 0.3s ease-out;
  animation: ${fadeIn} 5s ease-in-out;

  &:hover {
    transform: scale(1.05);
  }

  @media (max-width: 768px) {
    width: 90%;
    height: auto;
  }

  @media (max-width: 480px) {
    width: 100%;
    height: auto;
  }
`;

const LegalLinksContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  margin-top: 50px;
`;

const LegalLink = styled.div`
  color: #D4F1F4;
  font-size: 18px;
  margin: 10px;
  text-decoration: none;
  border-bottom: 1px solid transparent;
  cursor: pointer;
  transition: color 0.3s ease, border-bottom 0.3s ease;

  &:hover {
    color: #FFF;
    border-bottom: 1px solid #FFF;
  }
`;

const TermsOfServiceContainer = styled.div`
  padding: 40px;
  background: #1e1e1e;
  color: #ffffff;
  font-family: 'Poppins', sans-serif;
  line-height: 1.6;
`;

const TermsTitle = styled.h1`
  font-size: 32px;
  margin-bottom: 20px;
  text-align: center;
`;

const TermsContent = styled.div`
  font-size: 18px;
`;

export const About = () => {
  const [selectedTab, setSelectedTab] = useState("about");

  const renderContent = () => {
    switch (selectedTab) {
      case "about":
        return (
          <>
            <AboutText1>
              <div>ABOUT SOTD</div>
            </AboutText1>
            <AboutText2>
              <div>OUR MISSION</div>
            </AboutText2>
            <AboutText3>
              <div>
                At SOTD, we are committed to helping traders discover a more efficient way to trade by providing a streamlined, precise method for identifying top stocks for the day with a single click. By leveraging advanced AI technology, we generate insightful and data-driven analysis for each stock to assist in making informed trading decisions.
              </div>
            </AboutText3>
            <WideBullimg src={WideBull} />
            <AboutText4>
              <div>OUR TRADING STRATEGIES</div>
            </AboutText4>
            <AboutText8>
              <div>
                At SOTD, our trading strategies are enhanced by the power of AI-driven analysis, which includes screening stocks based on various technical indicators and market data. Each stock is evaluated with cutting-edge techniques, ensuring our clients receive the most relevant and timely insights.
              </div>
            </AboutText8>
          </>
        );
      case "terms":
        return (
          <TermsOfServiceContainer>
            <TermsTitle>Terms of Service</TermsTitle>
            <TermsContent>
              <p>
                Welcome to SOTD.ai, a service provided by Isharzi LLC. These Terms of Service ("Terms") govern your use of our website and services...
              </p>
              {/* Add more content as necessary */}
            </TermsContent>
          </TermsOfServiceContainer>
        );
      default:
        return null;
    }
  };

  return (
    <PageContainer>
      {renderContent()}
      <LegalLinksContainer>
        <LegalLink onClick={() => setSelectedTab("about")}>About</LegalLink>
        <LegalLink onClick={() => setSelectedTab("terms")}>Terms of Service</LegalLink>
        {/* Add other links if needed */}
      </LegalLinksContainer>
    </PageContainer>
  );
};
