import styled from 'styled-components';
import React, { useState, useEffect } from 'react';
import Logo from '../../WhiteLogo.PNG';
import Bull from '../../BullOnCliff.PNG';
import Phone from '../../AppPhone.PNG';
import Star from '../../star.png';

const DisclaimerWrapper = styled.div`
  display: ${(props) => (props.show ? 'flex' : 'none')};
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 1000;
`;

const DisclaimerBox = styled.div`
  background: white;
  padding: 20px;
  width: 70%;
  max-width: 500px;
  border-radius: 5px;
  position: relative;
  display: flex; // Set as a flex container
  flex-direction: column; // Vertical stacking
  align-items: center; // Center children horizontally
  justify-content: center; // Center children vertically
`;

const DisclaimerTitle = styled.h2`
  text-align: center;
  margin: 0;
  font-size: 30px;
  font-family: Ubuntu;
  font-weight: bold;
`;

const DisclaimerText = styled.p`
  font-size: 20px;
  font-family: Ubuntu;
  margin-top: 20px;
  text-align: center;
  width: 94%;
`;

const CloseButton = styled.span`
  position: absolute;
  right: 20px;
  top: 15px;
  cursor: pointer;
  font-size: 20px;
  color: grey;
`;

const AgreeButton = styled.button`
  background-color: black;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  font-size: 16px;
  font-family: Ubuntu;
  cursor: pointer;
  margin-top: 20px;
`;

const HomeText1 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 85px;
  font-family: Ubuntu;
  margin: 125px 0;
  font-weight: bold;
  color: #D4F1F4;
  margin-top: 200px;
  z-index: -1;
`;

const HomeText2 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 38px;
  font-family: Ubuntu;
  margin: -115px 0;
  color: white;
`;

const HomeText3 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 23px;
  font-family: Ubuntu;
  border-radius: 50px;
  border: 2px solid white; /* Border width and color */
  width: 20%;
  height: 4.3%;
  margin: 250px auto;
  padding: 4px;
  font-weight: bold;
  color: white; /* Set font color to white */
  cursor: pointer;
  position: relative;
  background-color: ${props => (props.selected ? 'white' : 'transparent')};

  &, a, a:visited, a:hover, a:active {
    color: white !important;
    text-decoration: none;
  }
  &:hover {
    background-color: #D4F1F4;
    color: black !important;
    cursor: pointer;
    transition: background-color 0.3s ease-out;
  }
`;

const HomeText4 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: left;
  align-items: left;
  flex-direction: column;
  font-size: 95px;
  font-family: Ubuntu;
  margin: 240px 0;
  position: relative;
  right: -37mm;
  font-weight: bold;
  color: white;
  top: 70px
`;

const HomeText5 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: left;
  align-items: left;
  flex-direction: column;
  font-size: 28px;
  font-family: Ubuntu;
  margin: -220px 0;
  position: relative;
  right: -37mm;
  color: #D4F1F4;
  top: 80px
`;

const HomeWrapper1 = styled.div`
  background: hsla(214, 44%, 17%, 1);
  background: linear-gradient(0deg, hsla(214, 44%, 17%, 1) 0%, hsla(216, 39%, 16%, 1) 0%, hsla(214, 40%, 22%, 1) 100%);
  background: -moz-linear-gradient(0deg, hsla(214, 44%, 17%, 1) 0%, hsla(216, 39%, 16%, 1) 0%, hsla(214, 40%, 22%, 1) 100%);
  background: -webkit-linear-gradient(0deg, hsla(214, 44%, 17%, 1) 0%, hsla(216, 39%, 16%, 1) 0%, hsla(214, 40%, 22%, 1) 100%);
  filter: progid: DXImageTransform.Microsoft.gradient( startColorstr="#192A40", endColorstr="#192639", GradientType=1 );

  background-size: cover;
  width: 100%;
  height: 176vh;
  position: absolute;
  top: 0;
`;

const Bullimg = styled.img`
  width: 39%;
  height: 39%;
  position: absolute;
  right: 950px;
  top: 340px;
`;

const Phoneimg = styled.img`
  width: 45%;
  height: 45%;
  position: absolute;
  right: -15px;
  top: 805px;
`;

const Starimg = styled.img`
  width: 45%;
  height: 45%;
  position: absolute;
  right: 105px;
  top: 85px;
  z-index: 0; // Set the z-index lower than HomeText1
`;

const LogoOnLoading = styled.img`
  width: 100px;
  height: 100px;
`;

export const Home = () => {
  const [clicked, setClicked] = useState(false);
  const [showDisclaimer, setShowDisclaimer] = useState(true);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000); // 1 second delay

    return () => clearTimeout(timer);
  }, []);

  const handleClick = () => {
    setClicked(!clicked);
  }

  const closeDisclaimer = () => {
    setShowDisclaimer(false);
    localStorage.setItem("disclaimerShown", "true");
  }

  const Disclaimer = ({ onClose }) => {
    return (
      <DisclaimerWrapper show={true}>
        <DisclaimerBox>
          <CloseButton onClick={onClose}>×</CloseButton>
          <DisclaimerTitle>DISCLAIMER</DisclaimerTitle>
          <DisclaimerText>
            Stock of The Day (SOTD) is a tool intended to assist you in identifying potential stocks to trade. However, it should not be the sole basis for your investment decisions. It's important to conduct your own research and consult with a financial advisor before making any trades. Please note that the regular SOTD only covers US stocks and all information is based on Eastern Standard Time (EST).
          </DisclaimerText>
          <AgreeButton onClick={onClose}>I AGREE</AgreeButton>
        </DisclaimerBox>
      </DisclaimerWrapper>
    );
  };

  if (loading) {
    return (
      <HomeWrapper1>
        <LogoOnLoading src={Logo} />
      </HomeWrapper1>
    );
  }

  return (
    <div>
      <br />
      <br />
      <br />
      <br />
      <br />
      {showDisclaimer && <Disclaimer onClose={closeDisclaimer} />}
      <HomeWrapper1>
        <Starimg src={Star} />
        <HomeText1>
          <div> Get the best, Earn the most </div>
        </HomeText1>
        <HomeText2>
          <div> Utilizing AI to generate </div>
          <div> top-tier stocks daily </div>
        </HomeText2>
        <HomeText3 onClick={handleClick}>
          <a href="/sotd" style={{ color: "black" }}>Today's watchlist</a>
        </HomeText3>
        <HomeText4>
          <div> We are </div>
          <div>Coming soon… </div>
        </HomeText4>
        <HomeText5>
          <div> SOTD app </div>
        </HomeText5>
        <Bullimg src={Bull} />
        <Phoneimg src={Phone} />
      </HomeWrapper1>
    </div>
  );
};
