import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import Logo from '../../WhiteLogo.PNG';

const PageContainer = styled.div`
  background-color: #4c4c4c;
  min-height: 101vh;
`;

export const SOTD = () => {
  const [data, setData] = useState({});
  const [contentLoaded, setContentLoaded] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/filtered_stocks');
        console.log('Fetched Data:', response.data);
        setData(response.data);
        setContentLoaded(true);
      } catch (err) {
        console.log('ERROR:', err);
        setError(err);
      }
    };
    fetchData();
  }, []);

  if (error) {
    return (
      <PageContainer>
        <div>Error fetching data</div>
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      {!contentLoaded ? (
        <div>Loading...</div>
      ) : (
        <div>
          <div>
            <div>Day-trading</div>
          </div>
          <div>
            <div>Top picks for today</div>
          </div>
          <ol>
            {data['Day Trading']?.map((stock, index) => (
              <li key={index}>
                <div>
                  <div>{stock.ticker}</div>
                  <div>${stock.price ? stock.price.toFixed(2) : 'N/A'}</div>
                </div>
                <div>
                  <div>{stock.name}</div>
                  <div>{stock.change_percent ? stock.change_percent.toFixed(2) : 'N/A'}%</div>
                </div>
                <hr />
                <div>
                  <div>
                    <span>VOLUME:</span> {stock.volume ? stock.volume.toLocaleString() : 'N/A'}
                  </div>
                  <div>
                    <span>HIGH:</span> {stock.Upper_BB ? stock.Upper_BB.toFixed(2) : 'N/A'}
                  </div>
                  <div>
                    <span>LOW:</span> {stock.Lower_BB ? stock.Lower_BB.toFixed(2) : 'N/A'}
                  </div>
                </div>
                <div>
                  <img src={Logo} alt="Logo" />
                </div>
                <div>{stock.ticker} NEWS</div>
              </li>
            ))}
          </ol>
        </div>
      )}
    </PageContainer>
  );
};
