import React, { useState } from 'react';
import { PageContainer, FormContainer, InputField, SubmitButton, FormTitle, FormText, FormLink } from '/Users/ismaelanwarzai/Desktop/SOTD/web/src/core/components/LoginSignupStyles.js';

const SignupPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      const response = await fetch('http://localhost:4243/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (!response.ok) {
        setError(data.error);
        return;
      }

      setSuccess('User registered successfully!');
      setError('');
      setEmail('');
      setPassword('');
      setConfirmPassword('');
    } catch (error) {
      setError('An error occurred. Please try again later.');
    }
  };

  return (
    <PageContainer>
      <FormContainer>
        <FormTitle>Sign Up</FormTitle>
        <form onSubmit={handleSubmit}>
          <InputField 
            type="email" 
            placeholder="Email" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required 
          />
          <InputField 
            type="password" 
            placeholder="Password" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required 
          />
          <InputField 
            type="password" 
            placeholder="Confirm Password" 
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required 
          />
          {error && <p style={{ color: 'red' }}>{error}</p>}
          {success && <p style={{ color: 'green' }}>{success}</p>}
          <SubmitButton type="submit">Sign Up</SubmitButton>
        </form>
        <FormText>
          Already have an account? <FormLink to="/login">Login</FormLink>
        </FormText>
      </FormContainer>
    </PageContainer>
  );
};

export default SignupPage;
