import React from "react";
import styled, { keyframes } from 'styled-components';
import WideBull from '../../WideBull.jpg';
import { useState, useEffect } from "react";

// Animation for fading in content
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1);
    transform: translateY(0);
  }
`;

// Animation for scaling on hover
const hoverScale = keyframes`
  from {
    transform: scale(1);
  }
  to {
    transform: scale(1.05);
  }
`;

const PageContainer = styled.div`
  background: linear-gradient(135deg, #0d0d0d 0%, #434343 100%);
  min-height: 220vh;
  padding-bottom: 50px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const AboutText1 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 75px;
  font-family: 'Poppins', sans-serif;
  font-weight: bold;
  margin-bottom: 70px;
  margin-top: 120px;
  padding: 10px;
  animation: ${fadeIn} 1s ease-in-out;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: white;
`;

const AboutText2 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 32px;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 20px;
  color: #b3b3b3;
  animation: ${fadeIn} 1.5s ease-in-out;
`;

const AboutText3 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 22px;
  font-family: 'Poppins', sans-serif;
  width: 66%;
  margin: auto;
  color: #D4F1F4;
  margin-bottom: 60px;
  animation: ${fadeIn} 2s ease-in-out;
  line-height: 1.6;
`;

const AboutText4 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  font-size: 32px;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 30px;
  color: #FFF;
  margin-top: 140px;
  animation: ${fadeIn} 2.5s ease-in-out;
`;

const AboutText8 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  font-size: 22px;
  font-family: 'Poppins', sans-serif;
  width: 66%;
  margin: auto;
  color: #D4F1F4;
  margin-bottom: 50px;
  animation: ${fadeIn} 3s ease-in-out;
  line-height: 1.6;
`;

const AboutTextContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  font-family: 'Poppins', sans-serif;
  margin: 35px 0;
  margin-top: 30px;
  animation: ${fadeIn} 3.5s ease-in-out;
`;

const AboutTextContainer2 = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  font-family: 'Poppins', sans-serif;
  margin: 35px 0;
  animation: ${fadeIn} 4s ease-in-out;
`;

const AboutText5 = styled.div`
  font-size: 22px; /* Reduced font size */
  border: 2px solid #D4F1F4;
  width: 20%; /* Increased button size */
  height: 15%; /* Increased button size */
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
  padding: 15px; /* Increased padding */
  color: ${props => (props.selected ? '#189AB4' : '#FFF')};
  background-color: ${props => (props.selected ? '#D4F1F4' : 'transparent')};
  cursor: pointer;
  transition: all 0.3s ease-out;
  position: relative;
  z-index: 1;
  text-align: center; /* Center the text */

  &:hover {
    animation: ${hoverScale} 0.3s forwards;
    color: #189AB4;
    background-color: #FFF;
    box-shadow: 0px 0px 15px 2px rgba(255, 255, 255, 0.2);
  }

  @media (max-width: 768px) {
    font-size: 18px;
  }
  @media (max-width: 576px) {
    font-size: 16px;
  }
`;

const AboutText6 = styled(AboutText5)`
  text-align: center; /* Center the text */
`;

const AboutText7 = styled(AboutText5)`
  text-align: center; /* Center the text */
`;


const NewDiv = styled.div`
  margin-top: 30px;
  font-size: 20px;
  width: 1000px;
  display: ${props => props.show ? 'flex' : 'none'};
  color: #FFF;
  text-align: left;
  padding: 10px 0;
  align-items: center;
  justify-content: center;
  flex-direction: row;
  flex-wrap: wrap;
  animation: ${fadeIn} 4.5s ease-in-out;

  @media (max-width: 768px) {
    font-size: 18px;
    width: 90%;
  }

  @media (max-width: 480px) {
    font-size: 16px;
    width: 100%;
  }
`;

const NewDiv1 = styled(NewDiv)`
`;

const NewDiv2 = styled(NewDiv)`
`;

const WideBullimg = styled.img`
  width: 65%;
  height: auto; /* Maintain aspect ratio */
  position: relative;
  margin: 60px auto 0;
  display: block;
  border-radius: 15px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.5);
  transition: transform 0.3s ease-out;
  animation: ${fadeIn} 5s ease-in-out;

  &:hover {
    transform: scale(1.05);
  }

  @media (max-width: 768px) {
    width: 90%;
    height: auto; /* Maintain aspect ratio */
  }

  @media (max-width: 480px) {
    width: 100%;
    height: auto; /* Maintain aspect ratio */
  }
`;

export const About = () => {
  const [showDiv, setShowDiv] = useState(true);
  const [showDiv1, setShowDiv1] = useState(false);
  const [showDiv2, setShowDiv2] = useState(false);
  const [selectedTab, setSelectedTab] = useState(0);

  useEffect(() => {
    localStorage.setItem("selectedTab", selectedTab);
  }, [selectedTab]);

  const handleTabClick = (index) => {
    setShowDiv(index === 0);
    setShowDiv1(index === 1);
    setShowDiv2(index === 2);
    setSelectedTab(index);
  };

  return (
    <PageContainer>
      <AboutText1>
        <div>ABOUT SOTD</div>
      </AboutText1>
      <AboutText2>
        <div>OUR MISSION</div>
      </AboutText2>
      <AboutText3>
        <div>
          At SOTD, our mission is to revolutionize the way traders engage with the stock market by offering a cutting-edge platform that delivers highly accurate and timely stock analysis. We are dedicated to simplifying the trading process, enabling traders of all levels to make informed decisions quickly and effectively. Through our advanced algorithms and state-of-the-art technology, we aim to identify the best stocks of the day with unparalleled precision, making it easier for our clients to capitalize on market opportunities.
        </div>
      </AboutText3>
      <WideBullimg src={WideBull} />
      <AboutText4>
        <div>OUR TRADING STRATEGIES</div>
      </AboutText4>
      <AboutText8>
        <div>
          At SOTD, we employ a sophisticated, data-driven approach to develop and refine our trading strategies. Our methodologies are grounded in extensive research and the use of advanced technology to screen and analyze stocks. By leveraging a wide array of technical indicators and market data, we ensure that our clients receive the most relevant, accurate, and timely insights. Our strategies are continuously optimized to adapt to changing market conditions, providing our clients with a robust toolset to navigate the complexities of stock trading.
        </div>
      </AboutText8>

      <AboutTextContainer>
        <AboutText5
          selected={selectedTab === 0}
          onClick={() => handleTabClick(0)}
        >
          Day Trading
        </AboutText5>
