import styled, { keyframes } from 'styled-components';
import Bull from '../../BullOnCliff.PNG';
import Phone from '../../AppPhone.PNG';
import React, { useState } from "react";

const shootingStar = keyframes`
  0% {
    opacity: 0;
    transform: translateX(-50px) translateY(-50px) rotate(45deg);
  }
  50% {
    opacity: 1;
    transform: translateX(150px) translateY(150px) rotate(45deg);
  }
  100% {
    opacity: 0;
    transform: translateX(300px) translateY(300px) rotate(45deg);
  }
`;

const howlEffect = keyframes`
  0%, 100% {
    transform: translateY(0px) rotate(0deg);
  }
  50% {
    transform: translateY(-10px) rotate(-5deg);
  }
`;

const soundWave = keyframes`
  0% {
    opacity: 0.5;
    transform: scale(1);
  }
  100% {
    opacity: 0;
    transform: scale(1.5);
  }
`;

const HomeWrapper1 = styled.div`
  background: hsla(214, 44%, 17%, 1);
  background: linear-gradient(0deg, hsla(214, 44%, 17%, 1) 0%, hsla(216, 39%, 16%, 1) 0%, hsla(214, 40%, 22%, 1) 100%);
  filter: progid: DXImageTransform.Microsoft.gradient(startColorstr="#192A40", endColorstr="#192639", GradientType=1);
  background-size: cover;
  width: 100%;
  height: 200vh;
  position: absolute;
  top: 0;

  @media (max-width: 768px) {
    height: 250vh;
  }
`;

const Bullimg = styled.img`
  width: 42%;
  height: auto;
  position: absolute;
  right: 62%;
  top: 16%;
  animation: ${howlEffect} 2s infinite;

  @media (max-width: 1024px) {
    width: 40%;
    right: 63.5%;
    top: 16%;
  }

  @media (max-width: 768px) {
    width: 60%;
    right: 45.5%;
    top: 10%;
  }

  @media (max-width: 480px) {
    width: 80%;
    right: 5%;
    top: 35%;
  }
`;

const SoundWave = styled.div`
  position: absolute;
  right: 52%;
  top: 10%;
  width: 100px;
  height: 100px;
  background-color: rgba(255, 255, 255, 0.2);
  border-radius: 50%;
  animation: ${soundWave} 1.5s infinite ease-out;
  z-index: -1;

  @media (max-width: 1024px) {
    right: 53.5%;
    top: 12%;
  }

  @media (max-width: 768px) {
    right: 35.5%;
    top: 5%;
  }

  @media (max-width: 480px) {
    right: 10%;
    top: 20%;
  }
`;

const Phoneimg = styled.img`
  width: 40%;
  height: auto;
  position: absolute;
  right: 5%;
  top: 45%;

  @media (max-width: 1024px) {
    width: 40%;
    right: 10%;
    top: 50%;
  }

  @media (max-width: 768px) {
    width: 50%;
    right: -5%;
    top: 40%;
  }

  @media (max-width: 480px) {
    width: 80%;
    right: 5%;
    top: 55%;
  }
`;

const ShootingStar = styled.div`
  width: 2px;
  height: 2px;
  background: white;
  box-shadow: 0 0 8px white, 0 0 10px white, 0 0 15px white, 0 0 25px white;
  position: absolute;
  top: 10%;
  left: 10%;
  animation: ${shootingStar} 2s infinite ease-in-out;
  transform-origin: left top;
`;

const DisclaimerWrapper = styled.div`
  display: ${(props) => (props.show ? 'flex' : 'none')};
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 1000;
`;

const DisclaimerBox = styled.div`
  background: white;
  padding: 20px;
  width: 90%;
  max-width: 500px;
  border-radius: 5px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const DisclaimerTitle = styled.h2`
  text-align: center;
  margin: 0;
  font-size: 2rem;
  font-family: Ubuntu;
  font-weight: bold;
`;

const DisclaimerText = styled.p`
  font-size: 1rem;
  font-family: Ubuntu;
  margin-top: 20px;
  text-align: center;
`;

const CloseButton = styled.span`
  position: absolute;
  right: 20px;
  top: 15px;
  cursor: pointer;
  font-size: 1.5rem;
  color: grey;
`;

const AgreeButton = styled.button`
  background-color: black;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  font-family: Ubuntu;
  cursor: pointer;
  margin-top: 20px;
`;

const HomeText1 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 5vw;
  font-family: Ubuntu;
  margin: 125px 0;
  font-weight: bold;
  color: #D4F1F4;
  margin-top: 200px;
  z-index: -1;

  @media (max-width: 768px) {
    font-size: 8vw;
    margin-top: 100px;
  }
`;

const HomeText2 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 2vw;
  font-family: Ubuntu;
  margin: -115px 0;
  color: white;

  @media (max-width: 768px) {
    font-size: 4vw;
  }
`;

const HomeText3 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 1.5vw;
  font-family: Ubuntu;
  border-radius: 50px;
  border: 2px solid white;
  width: 20%;
  height: 4.3%;
  margin: 250px auto;
  padding: 4px;
  font-weight: bold;
  color: white;
  cursor: pointer;
  position: relative;
  background-color: ${props => (props.selected ? 'white' : 'transparent')};

  &, a, a:visited, a:hover, a:active {
    color: white !important;
    text-decoration: none;
  }

  &:hover {
    background-color: #D4F1F4;
    color: black !important;
    cursor: pointer;
    transition: background-color 0.3s ease-out;
  }

  @media (max-width: 768px) {
    font-size: 3vw;
    width: 50%;
  }
`;

const HomeText4 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: left;
  align-items: left;
  flex-direction: column;
  font-size: 6vw;
  font-family: Ubuntu;
  margin: 240px 0;
  position: relative;
  font-weight: bold;
  color: white;
  top: 70px;

  @media (max-width: 768px) {
    font-size: 8vw;
    margin-top: 100px;
  }
`;

const HomeText5 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: left;
  align-items: left;
  flex-direction: column;
  font-size: 2vw;
  font-family: Ubuntu;
  margin: -220px 0;
  position: relative;
  color: #D4F1F4;
  top: 80px;

  @media (max-width: 768px) {
    font-size: 4vw;
  }
`;

export const Home = () => {
  const [clicked, setClicked] = useState(false);
  const [showDisclaimer, setShowDisclaimer] = useState(!sessionStorage.getItem("disclaimerShown"));

  const handleClick = () => {
    setClicked(!clicked);
  }

  const closeDisclaimer = () => {
    setShowDisclaimer(false);
    sessionStorage.setItem("disclaimerShown", "true");
  }

  const Disclaimer = ({ onClose }) => {
    return (
      <DisclaimerWrapper show={true}>
        <DisclaimerBox>
          <CloseButton onClick={onClose}>×</CloseButton>
          <DisclaimerTitle>DISCLAIMER</DisclaimerTitle>
          <DisclaimerText>
