import React from 'react';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe('your-public-key'); // Replace with your Stripe public key

const CheckoutButton = () => {
  const handleCheckout = async () => {
    const response = await fetch('http://localhost:4242/create-checkout-session', { method: 'POST' });
    const session = await response.json();

    const stripe = await stripePromise;
    stripe.redirectToCheckout({ sessionId: session.id });
  };

  return <button onClick={handleCheckout}>Pay to Access</button>;
};

export default CheckoutButton;
