const formatPrice = (price) => {
  if (!price) return 'N/A';
  let formattedPrice = price.toFixed(6);
  while (formattedPrice.endsWith('0') && !formattedPrice.endsWith('.00')) {
    formattedPrice = formattedPrice.slice(0, -1);
  }
  return formattedPrice;
};

