import React, { useState } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.7);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100;
`;

const ModalContent = styled.div`
  background-color: #1e1e1e;
  padding: 30px;
  border-radius: 10px;
  text-align: center;
  color: #ffffff;
`;

const ModalButton = styled.button`
  background-color: #28a745;
  color: white;
  padding: 10px 20px;
  margin: 10px;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
  &:hover {
    background-color: #218838;
  }
`;

const GuestModeOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.8);
  z-index: 2;
  filter: blur(5px);
`;

const AccessControl = ({ onAccessGranted }) => {
  const [isGuest, setIsGuest] = useState(false);

  const handlePayingCustomerClick = () => {
    onAccessGranted(false);
  };

  const handleGuestClick = () => {
    setIsGuest(true);
    onAccessGranted(true);
  };

  if (isGuest) {
    return <GuestModeOverlay />; // Blur the content for guest users
  }

  return (
    <ModalOverlay>
      <ModalContent>
        <h2>Welcome! How would you like to proceed?</h2>
        <ModalButton onClick={handlePayingCustomerClick}>Paying Customer</ModalButton>
        <ModalButton onClick={handleGuestClick}>Guest</ModalButton>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AccessControl;
