import styled from 'styled-components';
import HomeBackground1 from '../../Testtest.jpeg';
import Bull from '../../BullOnCliff.PNG';
import Phone from '../../AppPhone.PNG';
import React, { useState } from "react";


const HomeText1 = styled.div`
   display: flex;
   flex-wrap: wrap;
   justify-content: center;
   align-items: center;
   flex-direction: column;
   font-size: 80px;
   font-family: Ubuntu;
   margin: 125px 0;
   font-weight: bold;
   color: #D4F1F4;
`;


const HomeText2 = styled.div`
   display: flex;
   flex-wrap: wrap;
   justify-content: center;
   align-items: center;
   flex-direction: column;
   font-size: 33px;
   font-family: Ubuntu;
   margin: -115px 0;
   color: white;
`;


const HomeText3 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 23px;
  font-family: Ubuntu;
  border-radius: 50px;
  border: 2px solid white; /* Border width and color */
  width: 20%;
  height: 4.3%;
  margin: 250px auto;
  padding: 4px;
  font-weight: bold;
  color: white; /* Text color is white */
  background-color: transparent;
  cursor: pointer;
  position: relative; /* Add this line */

  background-color: ${props =>
    props.selected ? 'white' : 'transparent'};

  /* Remove text-decoration and underline */
  a {
    text-decoration: none;
    position: relative;

    &:hover {
      text-decoration: none;
      color: white;
      background-color: #D4F1F4;
      cursor: pointer;
      transition: background-color 0.3s ease-out;
    }

    &:active,
    &:focus {
      color: white;
    }
  }
`;







const HomeText4 = styled.div`
   display: flex;
   flex-wrap: wrap;
   justify-content: left;
   align-items: left;
   flex-direction: column;
   font-size: 90px;
   font-family: Ubuntu;
   margin: 240px 0;
   position: relative;
   right: -37mm;
   font-weight: bold;
   color: white;
   top: 60px
`;


const HomeText5 = styled.div`
   display: flex;
   flex-wrap: wrap;
   justify-content: left;
   align-items: left;
   flex-direction: column;
   font-size: 28px;
   font-family: Ubuntu;
   margin: -220px 0;
   position: relative;
   right: -37mm;
   color: #D4F1F4;
   top: 80px
`;


const HomeWrapper1 = styled.div`
background: hsla(214, 44%, 17%, 1);

background: linear-gradient(0deg, hsla(214, 44%, 17%, 1) 0%, hsla(216, 39%, 16%, 1) 0%, hsla(214, 40%, 22%, 1) 100%);

background: -moz-linear-gradient(0deg, hsla(214, 44%, 17%, 1) 0%, hsla(216, 39%, 16%, 1) 0%, hsla(214, 40%, 22%, 1) 100%);

background: -webkit-linear-gradient(0deg, hsla(214, 44%, 17%, 1) 0%, hsla(216, 39%, 16%, 1) 0%, hsla(214, 40%, 22%, 1) 100%);

filter: progid: DXImageTransform.Microsoft.gradient( startColorstr="#192A40", endColorstr="#192639", GradientType=1 );

  background-size: cover;
  width: 100%;
  height: 158vh;
  position: absolute;
`;

const Bullimg = styled.img`
width: 39%;
height: 39%;
position: absolute;
right: 950px;
top: 250px;
`;

const Phoneimg = styled.img`
width: 43%;
height: 45%;
position: absolute;
right: 45px;
top: 730px;
`;




export const Home = () => {
    const [clicked, setClicked] = useState(false);

    const handleClick = () => {
        setClicked(!clicked);
    }
    return (
        <div>
            <br/>
            <br/>
            <br/>
            <br/>
            <br/>
            <HomeWrapper1>
            <HomeText1>
                <div> Get the best, Earn the most </div>
            </HomeText1>
            <HomeText2>
                <div> Utilizing AI to generate </div>
                <div> top-tier stocks daily </div>
            </HomeText2>
            <HomeText3 onClick={handleClick}>
                <a href="/sotd"> Today's watchlist </a>
            </HomeText3>
            <HomeText4>
                <div> We are </div>
                <div>Coming soon… </div>
            </HomeText4>
            <HomeText5>
                <div> SOTD app </div>
            </HomeText5>
            <Bullimg src={Bull}/>; 
            <Phoneimg src={Phone}/>; 
            </HomeWrapper1>

        </div>
    );
};
