import React from 'react';
import styled, { keyframes } from 'styled-components';
import WideBull from '../../WideBull.jpg';
import { Link } from 'react-router-dom';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const PageContainer = styled.div`
  background: linear-gradient(135deg, #0d0d0d 0%, #434343 100%);
  min-height: 220vh;
  padding-bottom: 50px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const AboutText1 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 75px;
  font-family: 'Poppins', sans-serif;
  font-weight: bold;
  margin-bottom: 70px;
  margin-top: 120px;
  padding: 10px;
  animation: ${fadeIn} 1s ease-in-out;
  letter-spacing: 5px;
  text-transform: uppercase;
  color: white;
`;

const AboutText2 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 32px;
  font-family: 'Poppins', sans-serif;
  margin-bottom: 20px;
  color: #b3b3b3;
  animation: ${fadeIn} 1.5s ease-in-out;
`;

const AboutText3 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 22px;
  font-family: 'Poppins', sans-serif;
  width: 66%;
  margin: auto;
  color: #D4F1F4;
  margin-bottom: 60px;
  animation: ${fadeIn} 2s ease-in-out;
  line-height: 1.6;
`;

const LegalLinksContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  margin-top: 50px;
`;

const LegalLink = styled(Link)`
  color: #D4F1F4;
  font-size: 18px;
  margin: 10px;
  text-decoration: none;
  border-bottom: 1px solid transparent;
  transition: color 0.3s ease, border-bottom 0.3s ease;

  &:hover {
    color: #FFF;
    border-bottom: 1px solid #FFF;
  }
`;

const About = () => {
  return (
    <PageContainer>
      <AboutText1>ABOUT SOTD</AboutText1>
      <AboutText2>OUR MISSION</AboutText2>
      <AboutText3>
        At SOTD, we are committed to helping traders discover a more efficient way to trade by providing a streamlined, precise method for identifying top stocks for the day with a single click. By leveraging advanced AI technology, we generate insightful and data-driven analysis for each stock to assist in making informed trading decisions.
      </AboutText3>
      <LegalLinksContainer>
        <LegalLink to="/terms-of-service">Terms of Service</LegalLink>
        {/* Add more links here as necessary */}
      </LegalLinksContainer>
    </PageContainer>
  );
};

export default About;
