import styled, { keyframes } from 'styled-components';
import React, { useState } from "react";

// Keyframes for the bull running animation
const bullRun = keyframes`
  0% {
    transform: translateX(-150%) scale(0.8) rotateY(0deg);
  }
  25% {
    transform: translateX(-50%) scale(1) rotateY(0deg);
  }
  50% {
    transform: translateX(0%) scale(1.2) rotateY(0deg);
  }
  75% {
    transform: translateX(50%) scale(1) rotateY(0deg);
  }
  100% {
    transform: translateX(150%) scale(0.8) rotateY(180deg);
  }
`;

// Background animation
const backgroundShift = keyframes`
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 1000px 0;
  }
`;

const HomeWrapper1 = styled.div`
  background: linear-gradient(0deg, #111, #333);
  background-size: 200% 100%;
  animation: ${backgroundShift} 20s linear infinite;
  width: 100%;
  height: 200vh;
  position: absolute;
  top: 0;

  @media (max-width: 768px) {
    height: 250vh;
  }
`;

const BullAnimation = styled.div`
  width: 100px;
  height: 100px;
  background-color: transparent;
  position: absolute;
  bottom: 20%;
  left: 0;
  transform-origin: center;
  animation: ${bullRun} 10s infinite cubic-bezier(0.7, -0.5, 0.3, 1.5);

  &:before {
    content: '';
    position: absolute;
    width: 100px;
    height: 60px;
    background-color: brown;
    border-radius: 50% 50% 0 0;
    top: 0;
    left: 0;
  }

  &:after {
    content: '';
    position: absolute;
    width: 40px;
    height: 40px;
    background-color: brown;
    border-radius: 50%;
    top: -20px;
    left: 30px;
  }

  &::before {
    content: '';
    position: absolute;
    width: 20px;
    height: 20px;
    background-color: white;
    border-radius: 50%;
    top: -10px;
    left: 40px;
  }

  &::after {
    content: '';
    position: absolute;
    width: 20px;
    height: 20px;
    background-color: white;
    border-radius: 50%;
    top: -10px;
    left: 60px;
  }
`;

const HomeText1 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 5vw;
  font-family: Ubuntu;
  margin: 125px 0;
  font-weight: bold;
  color: #D4F1F4;
  margin-top: 200px;
  z-index: 1;

  @media (max-width: 768px) {
    font-size: 8vw;
    margin-top: 100px;
  }
`;

const HomeText2 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 2vw;
  font-family: Ubuntu;
  margin: -115px 0;
  color: white;

  @media (max-width: 768px) {
    font-size: 4vw;
  }
`;

export const Home = () => {
  const [showDisclaimer, setShowDisclaimer] = useState(!sessionStorage.getItem("disclaimerShown"));

  const closeDisclaimer = () => {
    setShowDisclaimer(false);
    sessionStorage.setItem("disclaimerShown", "true");
  };

  return (
    <div>
      <HomeWrapper1>
        <HomeText1>
          <div> Get the best, Earn the most </div>
        </HomeText1>
        <HomeText2>
          <div> Utilizing AI to generate </div>
          <div> top-tier stocks daily </div>
        </HomeText2>
        <BullAnimation />
      </HomeWrapper1>
    </div>
  );
};
