import styled, { keyframes } from 'styled-components';
import Bull from '../../BullOnCliff.PNG';
import Phone from '../../AppPhone.PNG';
import React, { useState } from "react";


const cinematicCamera = keyframes`
 from {
   perspective-origin: 60% 40%;
 }
 to {
   perspective-origin: 40% 60%;
 }
 from,
 to {
   opacity: 0;
 }
 25%,
 75% {
   opacity: 1;
 }
`;


const imagePop = keyframes`
 70%,
 100% {
   transform: translate3d(0, 0, 60px);
 }
`;


const cinematic = keyframes`
 from {
   transform: translateZ(-200px) rotateY(30deg) translateY(50vh);
 }
 to {
   transform: translateZ(-100px) rotateY(-30deg) translateY(-100%) translateY(50vh);
 }
`;


const Container = styled.div`
 perspective: 1200px;
 transform-style: preserve-3d;
 animation: ${cinematicCamera} 11s cubic-bezier(0.6, 0, 0.4, 1) both infinite;


 &:after {
   content: "";
   background: linear-gradient(to bottom, #000, #0000 20%, #0000 80%, #000);
   position: absolute;
   top: 0;
   left: 0;
   width: 100%;
   height: 100vh;
 }
`;


const Main = styled.main`
 background: #111;
 border: solid 1px #222;
 padding: 2rem;
 max-width: 100%;
 width: 960px;
 margin: 0 auto;
 display: grid;
 grid-template-columns: 1fr 1fr;
 grid-gap: 2rem;
 transform-origin: top center;
 transform-style: preserve-3d;
 animation: ${cinematic} 11s cubic-bezier(0.6, 0, 0.4, 1) both infinite;


 > * {
   grid-column: 1 / -1;
 }


 img {
   max-width: 100%;
   display: block;
   transform-style: preserve-3d;
   animation: ${imagePop} 11s cubic-bezier(0.6, 0, 0.4, 1) both infinite;


   &:last-of-type {
     animation-delay: 4s;
   }
 }
`;


const Header = styled.header`
 display: grid;
 align-content: center;
 grid-column: 1 / -1;


 h2.subheader {
   font-size: 2vw;
 }
`;


const Callout = styled.div`
 text-align: center;
 background-color: #3173fa;
 padding: 1vw 3vw;
  > p {
   color: white;
 }
`;


const StyledH1 = styled.h1`
 line-height: 1.3;
 font-size: 5vw;
 margin: 1rem 0;
`;


const StyledH2 = styled.h2`
 font-size: 4vw;
 margin: 1rem 0;
`;


const StyledH3 = styled.h3`
 font-size: 2vw;
 font-weight: bold;
`;


const StyledP = styled.p`
 line-height: 1.8;
 margin: 1rem 0;
 color: rgb(158, 166, 184);
`;


export const Home = () => {
 return (
   <Container>
     <Main>
       <Header>
         <StyledH2 className="subheader">— Keyframers</StyledH2>
         <StyledH1>Where we bring imaginative user interfaces <em>to life.</em></StyledH1>
         <StyledP>Lorem ipsum dolor sit amet consectetur adipisicing elit. Rerum suscipit ipsam aspernatur quasi reiciendis at eum cupiditate officiis repudiandae quae ea facere odit beatae voluptate recusandae quas, possimus laborum inventore.</StyledP>
       </Header>


       <img src="https://images.unsplash.com/photo-1558459654-c430be5b0a44?crop=entropy&cs=tinysrgb&fit=crop&fm=jpg&ixid=MXwxNDU4OXwwfDF8cmFuZG9tfHx8fHx8fHw&ixlib=rb-1.2.1&q=80&w=960&h=500" alt="" />


       <section>
         <StyledH2>The Client</StyledH2>
         <StyledP>You! Lorem ipsum dolor sit amet consectetur adipisicing elit. Totam voluptatum quidem eligendi debitis quia dignissimos ipsa error, atque quibusdam corrupti soluta facere nulla neque nostrum recusandae assumenda, aspernatur in. Provident!</StyledP>
       </section>


       <section>
         <StyledH2>Our Mission</StyledH2>
         <StyledP>To educate the world in web animation. Lorem ipsum dolor sit amet consectetur adipisicing elit. Blanditiis ipsa doloremque, natus dolorum a perferendis modi veritatis ab earum, culpa nemo, aliquam qui! Nostrum iste ullam voluptatem, doloribus odit autem.</StyledP>
       </section>


       <Callout>
         <StyledH3>Get animating!</StyledH3>
         <StyledP>Lorem ipsum, dolor sit amet consectetur adipisicing elit. Consequatur magni dolores iusto nulla vitae, reiciendis amet veritatis aliquam iste temporibus itaque aliquid, eveniet saepe reprehenderit distinctio eaque libero, culpa tenetur.</StyledP>
       </Callout>


       <img src='https://images.unsplash.com/photo-1603791445824-0050bd436b6d?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=MXwxNDU4OXwwfDF8cmFuZG9tfHx8fHx8fHw&ixlib=rb-1.2.1&q=80&w=960' alt='' />
     </Main>
   </Container>
 );
};
