import React, { useState, useEffect } from 'react';
import styled, { css, createGlobalStyle } from 'styled-components';
import { Link } from "react-router-dom";
import { ROUTES } from '../constants';
import Logo from '../../WhiteLogo.PNG';

const GlobalStyle = createGlobalStyle`
  body, html {
    margin: 0;
    padding: 0;
  }
`;

const NavLink = styled(Link)`
  font-size: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 10px 15px;
  color: white;
  text-decoration: none;
  transition: color 0.2s ease-out, background-color 0.2s ease-out;
  border-radius: 3px;
  border: 0px solid white;
  position: relative;
  z-index: 10;

  &:hover {
    background-color: white;
    color: #4c4c4c;
    cursor: pointer;
    transition: background-color 0.3s ease-out;
  }

  ${({ selected }) => selected && css`
    background-color: #f0f0f0;
    color: #4c4c4c;
  `}

  @media (max-width: 1200px) {
    font-size: 18px;
    padding: 8px 12px;
  }

  @media (max-width: 992px) {
    font-size: 16px;
    padding: 6px 10px;
  }

  @media (max-width: 768px) {
    font-size: 14px;
    padding: 4px 8px;
  }

  @media (max-width: 576px) {
    font-size: 18px;
    padding: 5px 10px;
  }
`;

const Logoimg = styled.img`
  display: flex;
  justify-content: center;
  width: 4%;
  max-width: 100px; /* Ensures the logo doesn't get too large */
  height: auto; /* Maintains aspect ratio */
  position: absolute;
  left: 20px;
  top: 10px;
  z-index: 10;

  @media (max-width: 768px) {
    width: 7%;
  }

  @media (max-width: 480px) {
    width: 10%;
  }
`;

const NavbarContainer = styled.div`
  background-color: #333; /* Set a background color for the navbar */
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 10;
  flex-wrap: wrap; /* Allow items to wrap */
  margin-left: 500px; /* Add margin to move tabs to the right */
  position: fixed;
  width: 100%;
  top: 0;
  left: 0;

  @media (max-width: 768px) {
    justify-content: space-around;
    margin-left: 100px; /* Add margin to move tabs to the right */
  }
`;

const PageContainer = styled.div`
  background-color: #f0f0f0; 
  padding-top: 60px; /* Adjust this value based on your navbar height */
`;

export const Navbar = () => {
  const [selectedTab, setSelectedTab] = useState(ROUTES.HOME_PATH);

  useEffect(() => {
    const currentPath = window.location.pathname;
    setSelectedTab(currentPath);
  }, []);

  const handleTabClick = (path) => {
    setSelectedTab(path);
  };

  return (
    <>
      <GlobalStyle />
      <PageContainer>
        <div>
          <Link to={ROUTES.HOME_PATH} onClick={() => handleTabClick(ROUTES.HOME_PATH)}>
            <Logoimg src={Logo} />
          </Link>
          <NavbarContainer>
            <NavLink to={ROUTES.HOME_PATH} onClick={() => handleTabClick(ROUTES.HOME_PATH)} selected={selectedTab === ROUTES.HOME_PATH}>
              HOME
            </NavLink>
            <NavLink to={ROUTES.SOTD_PATH} onClick={() => handleTabClick(ROUTES.SOTD_PATH)} selected={selectedTab === ROUTES.SOTD_PATH}>
              SOTD
            </NavLink>
            <NavLink to={ROUTES.ABOUT_PATH} onClick={() => handleTabClick(ROUTES.ABOUT_PATH)} selected={selectedTab === ROUTES.ABOUT_PATH}>
              ABOUT
            </NavLink>
            <NavLink to={ROUTES.CONTACT_PATH} onClick={() => handleTabClick(ROUTES.CONTACT_PATH)} selected={selectedTab === ROUTES.CONTACT_PATH}>
              CONTACT
            </NavLink>
          </NavbarContainer>
        </div>
      </PageContainer>
    </>
  );
};
