import React, { useState } from 'react';
import { PageContainer, FormContainer, InputField, SubmitButton, FormTitle, FormText, FormLink } from './LoginSignupStyles';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:4243/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (!response.ok) {
        setError(data.error);
        return;
      }

      // Store token in local storage (or use a cookie for security)
      localStorage.setItem('token', data.token);
      setError('');
      alert('Login successful!');

      // Redirect or do something after login
    } catch (error) {
      setError('An error occurred. Please try again later.');
    }
  };

  return (
    <PageContainer>
      <FormContainer>
        <FormTitle>Login</FormTitle>
        <form onSubmit={handleSubmit}>
          <InputField 
            type="email" 
            placeholder="Email" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required 
          />
          <InputField 
            type="password" 
            placeholder="Password" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required 
          />
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <SubmitButton type="submit">Login</SubmitButton>
        </form>
        <FormText>
          Don't have an account? <FormLink to="/signup">Sign Up</FormLink>
        </FormText>
      </FormContainer>
    </PageContainer>
  );
};

export default LoginPage;
