import React, { useState, useEffect } from 'react';
import styled, { css } from 'styled-components';
import { Link } from "react-router-dom";
import { ROUTES } from '../constants';
import Logo from '../../WhiteLogo.PNG';
import BlueLogo from '../../BlueLogo.PNG';

const NavLink = styled(Link)`
  font-size: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 10px 15px;
  color: ${({ $currentPath }) => $currentPath === ROUTES.HOME_PATH ? 'white' : 'black'};
  text-decoration: none;
  transition: color 0.2s ease-out, background-color 0.2s ease-out;
  border-radius: 3px;
  border: 0px solid white;
  position: relative;
  z-index: 10;

  &:hover {
    background-color: white;
    color: black;
    cursor: pointer;
    transition: background-color 0.3s ease-out;
  }

  ${({ selected }) => selected && css`
    background-color: #f0f0f0;
    color: #4c4c4c;
  `}

  @media (max-width: 1200px) {
    font-size: 18px;
    padding: 8px 12px;
  }

  @media (max-width: 992px) {
    font-size: 16px;
    padding: 6px 10px;
  }

  @media (max-width: 768px) {
    font-size: 14px;
    padding: 4px 8px;
  }

  @media (max-width: 576px) {
    font-size: 18px;
    padding: 5px 10px;
  }
`;

const Logoimg = styled.img`
  display: flex;
  justify-content: center;
  width: 3.5%;
  max-width: 100px; /* Ensures the logo doesn't get too large */
  height: auto; /* Maintains aspect ratio */
  position: absolute;
  left: 20px;
  top: 10px;
  z-index: 10;

  @media (max-width: 768px) {
    width: 7%;
  }

  @media (max-width: 480px) {
    width: 10%;
  }
`;

const NavbarContainer = styled.div`
  background-color: transparent;
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 10;
  flex-wrap: wrap; /* Allow items to wrap */
  margin-left: 500px;

  @media (max-width: 768px) {
    justify-content: space-around;
    margin-left: 100px; /* Add margin to move tabs to the right */
  }
`;

const PageContainer = styled.div`
  background-color: #f0f0f0; 
`;

const AuthContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  gap: 15px;

  @media (max-width: 768px) {
    justify-content: center;
    margin-top: 10px;
  }
`;

const AuthLink = styled(Link)`
  font-size: 18px;
  color: white; /* White text by default */
  background-color: black; /* Black background */
  text-decoration: none;
  padding: 10px 20px;
  border-radius: 20px;
  border: 2px solid black;
  transition: background-color 0.3s ease, color 0.3s ease;

  &:hover {
    background-color: white; /* White background on hover */
    color: black; /* Black text on hover */
  }

  @media (max-width: 768px) {
    font-size: 16px;
    padding: 8px 16px;
  }
`;

const ProfileContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
`;

const ProfileName = styled.span`
  color: white;
  font-size: 16px;
`;

const ProfileImage = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  border: 2px solid #4caf50;
`;

export const Navbar = () => {
  const [selectedTab, setSelectedTab] = useState(ROUTES.HOME_PATH);
  const [currentPath, setCurrentPath] = useState(window.location.pathname);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const currentPath = window.location.pathname;
    setSelectedTab(currentPath);
    setCurrentPath(currentPath);

    // Check if user is logged in
    const token = localStorage.getItem('token');
    if (token) {
      // Set user info (this is mocked for now, replace with actual API call)
      setUser({ name: 'John Doe', image: 'https://i.pravatar.cc/150?img=3' });
    }
  }, []);

  const handleTabClick = (path) => {
    setSelectedTab(path);
    setCurrentPath(path);
  };

  return (
    <PageContainer>
      <div>
        <Link to={ROUTES.HOME_PATH} onClick={() => handleTabClick(ROUTES.HOME_PATH)}>
          <Logoimg src={currentPath === ROUTES.HOME_PATH ? Logo : BlueLogo} />
        </Link>
        <NavbarContainer>
          <NavLink to={ROUTES.HOME_PATH} onClick={() => handleTabClick(ROUTES.HOME_PATH)} selected={selectedTab === ROUTES.HOME_PATH} $currentPath={currentPath}>
            HOME
          </NavLink>
          <NavLink to={ROUTES.SOTD_PATH} onClick={() => handleTabClick(ROUTES.SOTD_PATH)} selected={selectedTab === ROUTES.SOTD_PATH} $currentPath={currentPath}>
            SOTD
          </NavLink>
          <NavLink to={ROUTES.ABOUT_PATH} onClick={() => handleTabClick(ROUTES.ABOUT_PATH)} selected={selectedTab === ROUTES.ABOUT_PATH} $currentPath={currentPath}>
            ABOUT
          </NavLink>
          <NavLink to={ROUTES.CONTACT_PATH} onClick={() => handleTabClick(ROUTES.CONTACT_PATH)} selected={selectedTab === ROUTES.CONTACT_PATH} $currentPath={currentPath}>
            CONTACT
          </NavLink>
          <AuthContainer>
            {user ? (
              <ProfileContainer>
                <ProfileName>{user.name}</ProfileName>
                <ProfileImage src={user.image} alt="Profile" />
              </ProfileContainer>
            ) : (
              <>
                <AuthLink to="/login">Login</AuthLink>
                <AuthLink to="/signup">Sign Up</AuthLink>
              </>
            )}
          </AuthContainer>
        </NavbarContainer>
      </div>
    </PageContainer>
  );
};
