import styled, { keyframes } from 'styled-components';
import Bull from '../../BullOnCliff.PNG';
import Phone from '../../AppPhone.PNG';
import React, { useState } from "react";

// Enhanced shooting star animation
const shootingStar = keyframes`
  0% {
    opacity: 0;
    transform: translateX(-50px) translateY(-50px) rotate(45deg);
  }
  50% {
    opacity: 1;
    transform: translateX(150px) translateY(150px) rotate(45deg);
  }
  100% {
    opacity: 0;
    transform: translateX(300px) translateY(300px) rotate(45deg);
  }
`;

const HomeWrapper1 = styled.div`
  background: linear-gradient(145deg, #1f1c2c 0%, #928dab 100%);
  background-size: cover;
  width: 100%;
  height: 200vh;
  position: relative;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 50px 20px;

  @media (max-width: 768px) {
    height: auto;
    padding: 20px;
  }
`;

const Bullimg = styled.img`
  width: 35%;
  position: absolute;
  left: 5%;
  top: 20%;

  @media (max-width: 1024px) {
    width: 50%;
    top: 25%;
  }

  @media (max-width: 768px) {
    width: 70%;
    top: 30%;
    left: 10%;
  }
`;

const Phoneimg = styled.img`
  width: 35%;
  position: absolute;
  right: 5%;
  top: 50%;

  @media (max-width: 1024px) {
    width: 50%;
    top: 55%;
  }

  @media (max-width: 768px) {
    width: 70%;
    right: 10%;
  }
`;

const ShootingStar = styled.div`
  width: 5px;
  height: 5px;
  background: #f0f0f0;
  box-shadow: 0 0 8px #f0f0f0, 0 0 10px #f0f0f0, 0 0 15px #f0f0f0, 0 0 25px #f0f0f0;
  position: absolute;
  top: 20%;
  left: 20%;
  animation: ${shootingStar} 2s infinite ease-in-out;
  transform-origin: left top;
`;

const TextContainer = styled.div`
  text-align: center;
  color: #ffffff;
  margin-top: 50px;
  z-index: 2;
`;

const HomeText1 = styled.div`
  font-size: 4vw;
  font-family: 'Montserrat', sans-serif;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 3px;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    font-size: 6vw;
  }
`;

const HomeText2 = styled.div`
  font-size: 1.5vw;
  font-family: 'Montserrat', sans-serif;
  margin-bottom: 40px;
  line-height: 1.4;

  @media (max-width: 768px) {
    font-size: 3vw;
  }
`;

const HomeText3 = styled.a`
  display: inline-block;
  font-size: 1.5vw;
  font-family: 'Montserrat', sans-serif;
  border-radius: 50px;
  border: 2px solid white;
  padding: 10px 20px;
  color: white;
  cursor: pointer;
  text-decoration: none;
  background-color: transparent;
  transition: background-color 0.3s ease-out, color 0.3s ease-out;

  &:hover {
    background-color: #ffffff;
    color: #000000;
  }

  @media (max-width: 768px) {
    font-size: 4vw;
    padding: 12px 24px;
  }
`;

const HomeText4 = styled.div`
  font-size: 3vw;
  font-family: 'Montserrat', sans-serif;
  font-weight: 700;
  text-transform: uppercase;
  margin-top: 100px;

  @media (max-width: 768px) {
    font-size: 5vw;
  }
`;

const HomeText5 = styled.div`
  font-size: 1.5vw;
  font-family: 'Montserrat', sans-serif;
  color: #ffffff;
  margin-top: 10px;

  @media (max-width: 768px) {
    font-size: 3vw;
  }
`;

export const Home = () => {
  const [showDisclaimer, setShowDisclaimer] = useState(!sessionStorage.getItem("disclaimerShown"));

  const closeDisclaimer = () => {
    setShowDisclaimer(false);
    sessionStorage.setItem("disclaimerShown", "true");
  };

  return (
    <div>
      <HomeWrapper1>
        <ShootingStar />
        <TextContainer>
          <HomeText1>Get the best, Earn the most</HomeText1>
          <HomeText2>Utilizing AI to generate top-tier stocks daily</HomeText2>
          <HomeText3 href="/sotd">Today's watchlist</HomeText3>
          <HomeText4>We are</HomeText4>
          <HomeText5>Coming soon…</HomeText5>
        </TextContainer>
        <Bullimg src={Bull} />
        <Phoneimg src={Phone} />
      </HomeWrapper1>
    </div>
  );
};
