// SignupPage.js
import React, { useState } from 'react';

const SignupPage = ({ onSignup }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [plan, setPlan] = useState('basic'); // Default to basic plan
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:4243/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password, plan }), // Make sure plan is included
      });

      const data = await response.json();
      if (response.ok) {
        onSignup(plan);  // Log in user after signup
      } else {
        setError(data.error);
      }
    } catch (error) {
      setError('An error occurred. Please try again later.');
    }
  };


  return (
    <div>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <div>
          <label>
            <input
              type="radio"
              name="plan"
              value="basic"
              checked={plan === 'basic'}
              onChange={() => setPlan('basic')}
            />
            Basic (Free)
          </label>
          <label>
            <input
              type="radio"
              name="plan"
              value="premium"
              checked={plan === 'premium'}
              onChange={() => setPlan('premium')}
            />
            Premium ($10)
          </label>
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default SignupPage;
