import styled, { keyframes } from 'styled-components';
import Bull from '../../BullOnCliff.PNG';
import Phone from '../../AppPhone.PNG';
import React, { useState, useEffect } from "react";

// Keyframes for the cinematic animation
const cinematicCamera = keyframes`
 from {
   perspective-origin: 60% 40%;
 }
 to {
   perspective-origin: 40% 60%;
 }
 from,
 to {
   opacity: 0;
 }
 25%,
 75% {
   opacity: 1;
 }
`;

const imagePop = keyframes`
 70%,
 100% {
   transform: translate3d(0, 0, 60px);
 }
`;

const cinematic = keyframes`
 from {
   transform: translateZ(-200px) rotateY(30deg) translateY(50vh);
 }
 to {
   transform: translateZ(-100px) rotateY(-30deg) translateY(-100%) translateY(50vh);
 }
`;

const animStar = keyframes`
  from {
    transform: translateY(100vh);
  }
  to {
    transform: translateY(-100vh);
  }
`;

const fadeOut = keyframes`
  from {
    opacity: 1;
  }
  to {
    opacity: 0;
  }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

// Styled components for the cinematic animation
const Container = styled.div`
 perspective: 1200px;
 transform-style: preserve-3d;
 animation: ${cinematicCamera} 11s cubic-bezier(0.6, 0, 0.4, 1) both infinite;
 animation-fill-mode: forwards;
 animation: ${props => props.fadeOut ? fadeOut : cinematicCamera} 1s ease-in-out forwards;
 &:after {
   content: "";
   background: linear-gradient(to bottom, #000, #0000 20%, #0000 80%, #000);
   position: absolute;
   top: 0;
   left: 0;
   width: 100%;
   height: 100vh;
 }
`;

const Main = styled.main`
 background: #111;
 border: solid 1px #222;
 padding: 2rem;
 max-width: 100%;
 width: 960px;
 margin: 0 auto;
 display: grid;
 grid-template-columns: 1fr 1fr;
 grid-gap: 2rem;
 transform-origin: top center;
 transform-style: preserve-3d;
 animation: ${cinematic} 11s cubic-bezier(0.6, 0, 0.4, 1) both infinite;
 > * {
   grid-column: 1 / -1;
 }
 img {
   max-width: 100%;
   display: block;
   transform-style: preserve-3d;
   animation: ${imagePop} 11s cubic-bezier(0.6, 0, 0.4, 1) both infinite;
   &:last-of-type {
     animation-delay: 4s;
   }
 }
`;

const Header = styled.header`
 display: grid;
 align-content: center;
 grid-column: 1 / -1;
 h2.subheader {
   font-size: 2vw;
 }
`;

const Callout = styled.div`
 text-align: center;
 background-color: #3173fa;
 padding: 1vw 3vw;
  > p {
   color: white;
 }
`;

const StyledH1 = styled.h1`
 line-height: 1.3;
 font-size: 5vw;
 margin: 1rem 0;
`;

const StyledH2 = styled.h2`
 font-size: 4vw;
 margin: 1rem 0;
`;

const StyledH3 = styled.h3`
 font-size: 2vw;
 font-weight: bold;
`;

const StyledP = styled.p`
 line-height: 1.8;
 margin: 1rem 0;
 color: rgb(158, 166, 184);
`;

// Shooting Star component
const ShootingStar = styled.div`
  width: 2px;
  height: 2px;
  background: white;
  box-shadow: 0 0 8px white, 0 0 10px white, 0 0 15px white, 0 0 25px white;
  position: absolute;
  top: ${({ top }) => top}%;
  left: ${({ left }) => left}%;
  animation: ${animStar} 20s linear infinite;
  transform-origin: left top;
  animation-fill-mode: forwards;
`;

const HomeWrapper1 = styled.div`
  background: radial-gradient(ellipse at bottom, #1B2735 0%, #090A0F 100%);
  width: 100%;
  height: 200vh;
  position: absolute;
  top: 0;
  overflow: hidden;
  animation: ${fadeIn} 1s ease-in-out forwards;

  @media (max-width: 768px) {
    height: 250vh;
  }
`;

const Bullimg = styled.img`
  width: 42%;
  height: auto;
  position: absolute;
  right: 62%;
  top: 16%;

  @media (max-width: 1024px) {
    width: 40%;
    right: 63.5%;
    top: 16%;
  }

  @media (max-width: 768px) {
    width: 60%;
    right: 45.5%;
    top: 10%;
  }

  @media (max-width: 480px) {
    width: 80%;
    right: 5%;
    top: 35%;
  }
`;

const Phoneimg = styled.img`
  width: 40%;
  height: auto;
  position: absolute;
  right: 5%;
  top: 45%;

  @media (max-width: 1024px) {
    width: 40%;
    right: 10%;
    top: 50%;
  }

  @media (max-width: 768px) {
    width: 50%;
    right: -5%;
    top: 40%;
  }

  @media (max-width: 480px) {
    width: 80%;
    right: 5%;
    top: 55%;
  }
`;

const HomeText1 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
