import React, { useState } from 'react';
import styled from 'styled-components';
import Mail from '../../whitemail.png';

const PageContainer = styled.div`
  background-color: #4c4c4c;
  height: 102vh;
`;

const ContactText1 = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 75px;
  font-family: Ubuntu;
  font-weight: bold;
  margin-bottom: 70px;
  box-sizing: border-box;
  color: white;
  padding: 10px;
`;

const InputContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 5px;
  width: 33%;
  padding: 10px;
  padding-left: 30px; 
`;

const FirstNameInput = styled.input`
  border: 1px solid white;
  height: 50px;
  width: 214px;
  border-radius: 15px;
  font-size: 20px;
  color: white;
  background: #4c4c4c;
  margin-left: 67px;

  &:focus {
    outline: none; 
    border-color: white; 
    border: 2px solid white;
  }
`;

const LastNameInput = styled.input`
  border: 1px solid white;
  height: 50px;
  width: 214px;
  border-radius: 15px;
  font-size: 20px;
  color: white;
  margin-left: 44px; 
  background: #4c4c4c; 
  &:focus {
    outline: none; 
    border-color: white; 
    border: 2px solid white;
  }
`;

const NameText = styled.div`
  font-size: 22px;
  text-align: left;
  color: white;
  position: relative;
  margin-right: 20px;
  left: 82px; 
`;

const LastNameText = styled(NameText)`
  left: 57px;  
`;

const EmailText = styled.div`
  font-size: 22px;
  text-align: left;
  color: white;
  position: relative;
  right: -110px;
`;

const MessageText = styled.div`
  font-size: 22px;
  text-align: left;
  color: white;
  position: relative;
  right: -110px;
`;

const MailImg = styled.img`
  width: 30%;
  height: 44%;
  position: absolute;
  right: 220px;
  top: 300px;
`;

const EmailInput = styled.input`
  border: 1px solid white;
  height: 50px;
  width: 480px;
  border-radius: 15px;
  font-size: 20px;
  margin-left: 95px;
  color: white;
  background: #4c4c4c; 
  &:focus {
    outline: none; 
    border-color: white; 
    border: 2px solid white;
  }
`;

const MessageInput = styled.textarea`
  border: 1px solid white;
  height: 100px;
  width: 480px;
  border-radius: 15px;
  font-size: 20px;
  vertical-align: top;
  align-self: flex-start;
  margin-left: 95px;
  color: white;
  background: #4c4c4c; 
  &:focus {
    outline: none; 
    border-color: white; 
    border: 2px solid white;
  }
`;

const SubmitButton = styled.button`
  top: 30px;
  height: 80px;
  position: relative;
  right: -110px;
  color: white;
  font-size: 22px;
  background-color: #4c4c4c;
  border: 1px solid #4c4c4c;
  transition: background-color 0.2s ease;
  cursor: pointer;
  padding: 1px 10px;
  &:hover {
    background-color: lightgray;
    color: #4c4c4c;
  }
`;

const ImageContainer = styled.div`
  display: inline-block;
  vertical-align: top;
`;

const LabelInputWrapper = styled.div`
  position: relative;
  margin-right: 40px; 
`;

export const Contact = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState("SUBMIT");

  const handleSubmit = async (event) => {
    event.preventDefault();

    setStatus("Sending...");
    let details = {
      name: `${firstName} ${lastName}`,
      email: email,
      message: message,
    };

    fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(response.statusText);
      }
      return response.json();
    })
    .then(result => {
      setStatus("SUBMIT");
      setFirstName("");
      setLastName("");
      setEmail("");
      setMessage("");
      alert(result.status);
      sendEmail(details); // Call sendEmail function here
    })
    .catch(error => console.error('Error:', error));

    setStatus("Submit");
  };

  const sendEmail = (details) => {
    fetch("https://api.emailservice.com/send", { // Replace with the actual email service API endpoint
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify({
        to: "ianwarzai909@gmail.com",
        subject: "Contact Form Submission",
        body: `
          Name: ${details.name}
          Email: ${details.email}
          Message: ${details.message}
        `
      }),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(response.statusText);
      }
      return response.json();
    })
    .then(result => {
      console.log("Email sent:", result);
    })
    .catch(error => console.error('Error:', error));
  };

  return (
    <div>
      <PageContainer>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
      <ContactText1>GET IN TOUCH</ContactText1>
      <form onSubmit={handleSubmit}>
        <InputContainer>
          <div>
            <NameText>First Name:</NameText>
            <FirstNameInput
              type="text"
              value={firstName}
              onChange={(event) => setFirstName(event.target.value)}
            />
          </div>
          <div>
          <LastNameText>Last Name:</LastNameText>
          <LastNameInput
            type="text"
            value={lastName}
            onChange={(event) => setLastName(event.target.value)}
          />
        </div>
        </InputContainer>
      <br />
        <LabelInputWrapper>
          <EmailText>Email:</EmailText>
          <EmailInput
            type="email"
            value={email}
            onChange={(event) => setEmail(event.target.value)}
          />
        </LabelInputWrapper>

        <br />
        <LabelInputWrapper>
          <MessageText>Message:</MessageText>
          <MessageInput
            type="text"
            value={message}
            onChange={(event) => setMessage(event.target.value)}
          />
        </LabelInputWrapper>
        <br />
        <SubmitButton type="submit">{status}</SubmitButton> 
      </form>
            <ImageContainer>
        <MailImg src={Mail} alt="Submit" />
      </ImageContainer> 
      </PageContainer>
    </div>
  );
};
