import React, { useState, useEffect } from 'react';
import styled, { css } from 'styled-components';
import { Link } from "react-router-dom";
import { ROUTES } from '../constants';
import Logo from '../../WhiteLogo.PNG';
//import ToggleSwitch from './ToggleSwitch';

const NavLink = styled(Link)`
  font-size: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2px 7px;
  color: white;
  text-decoration: none;
  transition: color 0.2s ease-out, background-color 0.2s ease-out;
  border-radius: 3px;
  border: 0px solid white;
  position: relative;
  z-index: 10;

  &:after {
    content: '';
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    bottom: -3px;
    width: 0;
    height: 1px;
    background-color: #f0f0f0;
    transition: width 0.3s ease-out;
    color: #4c4c4c;
  }

  &:hover {
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: white;
    color: #4c4c4c;
    cursor: pointer;
    transition: background-color 0.3s ease-out;
    flex-direction: row;
  }

  ${({ selected }) => selected && css`
    &:after {
      width: 100%;
    }
    background-color: #f0f0f0;
    color: #f0f0f0;
  `}
`;


const Logoimg = styled.img`
  display: flex;
  justify-content: center;
  width: 4%;
  max-width: 100px; /* Ensures the logo doesn't get too large */
  height: auto; /* Maintains aspect ratio */
  position: absolute;
  left: 20px;
  top: 10px; /* Use top instead of margin-top for better control */
  z-index: 10;

  @media (max-width: 768px) {
    width: 8%;
  }

  @media (max-width: 480px) {
    width: 10%;
  }
`;


const NavbarContainer = styled.div`
  background-color: transparent;
  width: 35%;
  padding: 22px 10px; 
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-left: 55%;
  margin-bottom: -85px;
  z-index: 10;
  margin-top: 10px;
`;

const PageContainer = styled.div`
  background-color: #f0f0f0; /* Set your desired gray background color here */
`;


export const Navbar = () => {
  const [selectedTab, setSelectedTab] = useState(ROUTES.HOME_PATH);

  useEffect(() => {
    const currentPath = window.location.pathname;
    setSelectedTab(currentPath);
  }, []);

  const handleTabClick = (path) => {
    setSelectedTab(path);
  };

  return (
    <PageContainer>
    <div>
    <Link to={ROUTES.HOME_PATH} onClick={() => handleTabClick(ROUTES.HOME_PATH)}>
          <Logoimg src={Logo} />
        </Link>
      <NavbarContainer>
        <NavLink to={ROUTES.HOME_PATH} onClick={() => handleTabClick(ROUTES.HOME_PATH)} style={selectedTab === ROUTES.HOME_PATH ? { backgroundColor: 'white', color: '#4c4c4c' } : {}}>
          HOME
        </NavLink>
        <NavLink to={ROUTES.SOTD_PATH} onClick={() => handleTabClick(ROUTES.SOTD_PATH)} style={selectedTab === ROUTES.SOTD_PATH ? { backgroundColor: 'white', color: '#4c4c4c' } : {}}>
          SOTD
        </NavLink>
        <NavLink to={ROUTES.ABOUT_PATH} onClick={() => handleTabClick(ROUTES.ABOUT_PATH)} style={selectedTab === ROUTES.ABOUT_PATH ? { backgroundColor: 'white', color: '#4c4c4c' } : {}}>
          ABOUT
        </NavLink>
        <NavLink to={ROUTES.CONTACT_PATH} onClick={() => handleTabClick(ROUTES.CONTACT_PATH)} style={selectedTab === ROUTES.CONTACT_PATH ? { backgroundColor: 'white', color: '#4c4c4c' } : {}}>
          CONTACT
        </NavLink>
        {/* <ToggleSwitch darkMode={darkMode} toggleTheme={toggleTheme} /> */}
      </NavbarContainer>
    </div>
    </PageContainer>
  );
};
