import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FormContainer, InputField, SubmitButton, FormTitle, FormText, FormLink } from '/Users/ismaelanwarzai/Desktop/SOTD/web/src/core/components/LoginSignupStyles.js';

const SignupPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSignup = (e) => {
    e.preventDefault();
    // Handle signup logic here
  };

  return (
    <FormContainer>
      <FormTitle>Sign Up</FormTitle>
      <form onSubmit={handleSignup}>
        <InputField
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <InputField
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <InputField
          type="password"
          placeholder="Confirm Password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          required
        />
        <SubmitButton type="submit">Sign Up</SubmitButton>
      </form>
      <FormText>
        Already have an account? <FormLink to="/login">Login</FormLink>
      </FormText>
    </FormContainer>
  );
};

export default SignupPage;
