import React, { useState } from 'react';
import { PageContainer, FormContainer, InputField, SubmitButton, FormTitle, FormText, FormLink } from './LoginSignupStyles';

const SignupPage = () => {
  // State to capture form input values
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validation for matching passwords
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    // Call backend API or handle signup logic here
    // You can replace this with your actual backend call
    console.log('Signing up with:', { email, password });

    // Reset form after successful submission
    setEmail('');
    setPassword('');
    setConfirmPassword('');
    setError('');
  };

  return (
    <PageContainer>
      <FormContainer>
        <FormTitle>Sign Up</FormTitle>
        <form onSubmit={handleSubmit}>
          <InputField 
            type="email" 
            placeholder="Email" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required 
          />
          <InputField 
            type="password" 
            placeholder="Password" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required 
          />
          <InputField 
            type="password" 
            placeholder="Confirm Password" 
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required 
          />
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <SubmitButton type="submit">Sign Up</SubmitButton>
        </form>
        <FormText>
          Already have an account? <FormLink to="/login">Login</FormLink>
        </FormText>
      </FormContainer>
    </PageContainer>
  );
};

export default SignupPage;
