import React, { useState, useEffect } from "react";
import styled, { keyframes } from 'styled-components';
import HomeBackground2 from '../../HomeBackground2.JPEG';
import WideBull from '../../WideBull.jpg';

// Keyframes for rotating the planets around the sun
const rotate = (radius, deg = 360) => keyframes`
  from {
    transform: rotate(0deg) translateY(-${radius}px) rotate(0deg);
  }
  to {
    transform: rotate(360deg) translateY(-${radius}px) rotate(-${deg}deg);
  }
`;

const PageContainer = styled.div`
  background-image: url('https://www.nasa.gov/sites/default/files/thumbnails/image/stsci-h-p2016a-m-2000x1500.png'); /* Example galaxy image */
  background-size: cover;
  background-position: center;
  min-height: 220vh;
  padding-bottom: 50px;
  position: relative; /* Ensure the Orrery is positioned correctly */
  overflow: hidden; /* To handle any overflow from the Orrery */
`;

const TextContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-family: Ubuntu;
  color: white;
  margin-bottom: 70px;
  padding: 10px;
`;

const LargeText = styled(TextContainer)`
  font-size: 75px;
  font-weight: bold;
`;

const MediumText = styled(TextContainer)`
  font-size: 32px;
  margin-bottom: 20px;
`;

const SmallText = styled(TextContainer)`
  font-size: 22px;
  width: 66%;
  color: #D4F1F4;
  margin: auto;
  margin-bottom: 60px;
`;

const RowText = styled(TextContainer)`
  flex-direction: row;
  margin-top: 140px;
`;

const TabContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 35px 0;
`;

const TabButton = styled.div`
  font-size: 28px;
  border: 2px solid #D4F1F4;
  width: 15%;
  height: 10%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 40px;
  padding: 5px;
  color: ${props => (props.selected ? '#189AB4' : 'white')};
  background-color: ${props => (props.selected ? '#D4F1F4' : 'transparent')};
  cursor: pointer;
  transition: all 0.3s ease-out;

  &:hover {
    background-color: #D4F1F4;
    color: #189AB4;
  }

  @media (max-width: 768px) {
    font-size: 24px;
  }

  @media (max-width: 576px) {
    font-size: 20px;
  }
`;

const ContentDiv = styled.div`
  margin-top: 30px;
  font-size: 20px;
  width: 1000px;
  color: white;
  display: ${props => props.show ? 'flex' : 'none'};
  text-align: left;
  padding: 10px 0;
  align-items: center;
  justify-content: center;

  @media (max-width: 768px) {
    font-size: 18px;
    width: 90%;
  }

  @media (max-width: 480px) {
    font-size: 16px;
    width: 100%;
  }
`;

const WideBullImage = styled.img`
  width: 65%;
  margin: 60px auto 0;
  display: block;

  @media (max-width: 768px) {
    width: 90%;
  }

  @media (max-width: 480px) {
    width: 100%;
  }
`;

// Orrery (3D solar system) components
const System = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  transform: scale(0.75);
`;

const Sun = styled.div`
  width: 144px;
  height: 144px;
  border-radius: 72px;
  position: absolute;
  top: 50%;
  left: 50%;
  margin: -72px;
  background-image: url('http://sdo.gsfc.nasa.gov/assets/img/latest/latest_256_HMIIF.jpg');
  background-size: cover;
`;

const Planet = styled.div`
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  background-color: ${props => props.color};
  position: absolute;
  top: 50%;
  left: 50%;
  margin: -${props => props.size / 2}px;
  animation: ${props => rotate(props.radius, 360)} ${props => props.speed}s infinite linear;
`;

const OrbitPath = styled.div`
  width: ${props => props.radius * 2}px;
  height: ${props => props.radius * 2}px;
  border-radius: 50%;
  position: absolute;
  top: 50%;
  left: 50%;
  margin: -${props => props.radius}px;
  border: solid 1px #444;
`;

export const About = () => {
  const [selectedTab, setSelectedTab] = useState(0);
  const [showContent, setShowContent] = useState([true, false, false]);

  useEffect(() => {
    localStorage.setItem("selectedTab", selectedTab);
  }, [selectedTab]);

  const handleTabClick = (index) => {
    const updatedShowContent = [false, false, false];
    updatedShowContent[index] = true;
    setShowContent(updatedShowContent);
    setSelectedTab(index);
  };

  return (
    <PageContainer>
      {/* Orrery background */}
      <System>
        <Sun />
        <OrbitPath radius={144} />
        <Planet radius={144} size={12} color="#888" speed={8.8} />
        <OrbitPath radius={180} />
        <Planet radius={180} size={14} color="#f5f9be" speed={22.5} />
        <OrbitPath radius={220} />
        <Planet radius={220} size={16} color="#4b94f9" speed={36.5} />
        <OrbitPath radius={270} />
        <Planet radius={270} size={18} color="#dd411a" speed={68.7} />
        <OrbitPath radius={480} />
        <Planet radius={480} size={35} color="#eaad3b" speed={433.2} />
        <OrbitPath radius={600} />
        <Planet radius={600} size={29} color="#d6cd93" speed={1075.9} />
        <OrbitPath radius={800} />
        <Planet radius={800} size={13} color="#bfeef2" speed={3068.7} />
        <OrbitPath radius={950} />
        <Planet radius={950} size={12} color="#363ed7" speed={6019} />
        <OrbitPath radius={1100} />
        <Planet radius={1100} size={6} color="#963" speed={9046.5} />
      </System>

      {/* Your existing content */}
      <LargeText>
        <div>ABOUT SOTD</div>
      </LargeText>
      <MediumText>
        <div>OUR MISSION</div>
      </MediumText>
      <SmallText>
        <div>
          At SOTD, we are committed to helping traders discover a more efficient way to trade by providing a streamlined, precise method for identifying top stocks for the day with a single click. We are passionate about this goal and excited to make it easier for everyone to detect top stocks through our software and community.
        </div>
      </SmallText>
      <WideBullImage src={WideBull} />
      <RowText>
        <div>OUR TRADING STRATEGIES</div>
      </RowText>
      <SmallText>
        <div>
          At SOTD, we employ a concise data-driven approach in the development of our trading strategies, incorporating extensive research and advanced AI technology to screen the best stocks for our clients. Please note that these are not the only criteria and indicators used for evaluating stocks in our screening process.
        </div>
      </SmallText>

      <TabContainer>
        <TabButton
          selected={selectedTab === 0}
          onClick={() => handleTabClick(0)}
        >
          Day-Trading
        </TabButton>
        <TabButton
          selected={selectedTab === 1}
          onClick={() => handleTabClick(1)}
        >
          Swing-Trading
        </TabButton>
        <TabButton
          selected={selectedTab === 2}
          onClick={() => handleTabClick(2)}
        >
          Long-term
