import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import Logo from '../../WhiteLogo.PNG';

const PageContainer = styled.div`
  background-color: #4c4c4c;
  min-height: 101vh;
`;

const ContactTextContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  font-family: Ubuntu;
  margin: 85px 0;
`;

const ContactText4 = styled.div`
  font-size: 80px;
  text-align: center;
  margin-top: 4cm;
  font-weight: bold;
  color: #D4F1F4;
`;

const TickerTextFirst = styled.div`
  font-size: 40px;
  color: #595959;
  margin-left: 20px;
  display: inline-block;
`;

const Item = styled.li`
  font-size: 40px;
  width: 38%;
  margin: 0 auto;
  margin-top: 90px;
  color: #595959;
`;

const Subtitle = styled.div`
  font-size: 20px;
  color: #A9A9A9;
  margin-left: 20px;
  display: inline-block;
`;

const Line = styled.hr`
  border: none;
  height: 2px;
  background-color: white;
  margin: 25px 0;
`;

const Container = styled.ol`
  color: #595959;
`;

const ItemRow = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
`;

const Price = styled.div`
  font-size: 30px;
  display: flex;
  justify-content: flex-end;
  font-weight: bold;
  color: white;
  align-items: center;
`;

const Change = styled.div`
  font-size: 18px;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  color: #07CE07;
  margin-top: -14px;
`;

const PriceChangeContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
`;

const VolumeText = styled.div`
  font-size: 23px;
  color: white;
  margin-top: 30px;
  margin-left: 10px;
  display: flex;
`;

const HighText = styled.div`
  font-size: 23px;
  color: white;
  margin-top: 30px;
  margin-left: 10px;
  display: flex;
`;

const LowText = styled.div`
  font-size: 23px;
  color: white;
  margin-top: 30px;
  margin-left: 10px;
  display: flex;
`;

const LogoImage = styled.img`
  height: 100px;
  width: 100px;
  margin-top: 30px;
`;

const DetailsContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
`;

const InfoContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: flex-start;
  width: 70%;
`;

const LogoContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  width: 30%;
`;

const TextLabel = styled.span`
  margin-right: 5px;
`;

const NewsHeader = styled.div`
  font-size: 20px;
  color: white;
  text-align: center;
  margin-top: 50px;
  font-weight: bold;
  cursor: pointer;
`;

const ContactText = styled.div`
  font-size: 28px;
  border: 2px solid #D4F1F4;
  width: 19%;
  height: 14%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 40px;
  padding: 10px 10px;
  color: white;
  box-sizing: border-box;
  flex-direction: row;
  background-color: ${props => (props.selected ? 'white' : 'transparent')};
  color: ${props => (props.selected ? 'white' : 'white')};
  &:hover {
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #D4F1F4;
    color: #4c4c4c;
    cursor: pointer;
    transition: background-color 0.3s ease-out;
    flex-direction: row;
  }
`;

const LoadButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  flex-direction: column;
`;

const LoadButton = styled.button`
  padding: 20px;
  border: 2px solid white;
  background: transparent;
  color: white;
  font-size: 24px;
  cursor: pointer;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 30px;
  &:hover {
    background-color: grey;
    border-color: grey;
    cursor: pointer;
    img {
      filter: brightness(0.7);
    }
  }
`;

const LogoOnButton = styled.img`
  height: 100px;
  width: 100px;
`;

const LoadButtonText = styled.div`
  position: absolute;
  top: -40px;
  font-size: 20px;
  color: white;
`;

const LoadingText = styled.div`
  margin-top: 20px;
  font-size: 20px;
  color: white;
`;

export const SOTD = () => {
  const [data, setData] = useState({});
  const [contentLoaded, setContentLoaded] = useState(false);
  const [loadingStep, setLoadingStep] = useState(0);

  useEffect(() => {
    axios
      .get('http://localhost:5001/api/filtered_stocks')
      .then((response) => {
        console.log('SUCCESS', response);
        setData(response.data);
        setContentLoaded(true);
      })
      .catch((error) => {
        console.log('ERROR:', error);
      });
  }, []);

  const loadContent = () => {
    setLoadingStep(1);
    setTimeout(() => {
      setLoadingStep(2);
      setTimeout(() => {
        setContentLoaded(true);
      }, 500);
    }, 500);
  };

  return (
    <PageContainer>
      {!contentLoaded ? (
        <LoadButtonContainer>
          <LoadButton onClick={loadContent}>
            <LoadButtonText>Tap to view</LoadButtonText>
            <LogoOnButton src={Logo} />
          </LoadButton>
          {loadingStep === 0 && <LoadingText>Stock of the day</LoadingText>}
          {loadingStep === 1 && <LoadingText>Initializing real-time data...</LoadingText>}
          {loadingStep === 2 && <LoadingText>Calculating short-float...</LoadingText>}
        </LoadButtonContainer>
      ) : (
        <div>
          <ContactTextContainer>
            <ContactText>
              <div>Day-trading</div>
            </ContactText>
          </ContactTextContainer>
          <ContactText4>
            <div>Top picks for today</div>
          </ContactText4>
          <Container>
            {data['Day Trading']?.map((stock, index) => (
              <Item key={index}>
                <ItemRow>
                  <TickerTextFirst>{stock.ticker}</TickerTextFirst>
                  <PriceChangeContainer>
                    <Price>${stock.price}</Price>
                  </PriceChangeContainer>
                </ItemRow>
                <ItemRow>
                  <Subtitle>{stock.name}</Subtitle>
                  <PriceChangeContainer>
                    <Change>{stock.change_percent}%</Change>
                  </PriceChangeContainer>
                </ItemRow>
                <Line />
                <DetailsContainer>
                  <InfoContainer>
                    <VolumeText>
                      <TextLabel>VOLUME:</TextLabel> {stock.volume}
                    </VolumeText>
                    <HighText>
                      <TextLabel>HIGH:</TextLabel> {stock.Upper_BB}
                    </HighText>
                    <LowText>
                      <TextLabel>LOW:</TextLabel> {stock.Lower_BB}
                    </LowText>
                  </InfoContainer>
                  <LogoContainer>
                    <LogoImage src={Logo} />
                  </LogoContainer>
                </DetailsContainer>
                <NewsHeader>{stock.ticker} NEWS</NewsHeader>
              </Item>
            ))}
          </Container>
        </div>
      )}
    </PageContainer>
  );
};
