import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import Logo from '../../WhiteLogo.PNG';

const PageContainer = styled.div`
  background-color: #4c4c4c;
  min-height: 101vh;
`;

const LoadingText = styled.div`
  margin-top: 20px;
  font-size: 20px;
  color: white;
`;

const TickerText = styled.div`
  font-size: 20px;
  color: white;
`;

export const SOTD = () => {
  const [data, setData] = useState(null);
  const [contentLoaded, setContentLoaded] = useState(false);

  useEffect(() => {
    axios
      .get('http://localhost:5001/api/filtered_stocks')
      .then((response) => {
        console.log('Fetched Data:', response.data);
        setData(response.data);
        setContentLoaded(true);
      })
      .catch((error) => {
        console.log('ERROR:', error);
      });
  }, []);

  console.log('State Data:', data);

  return (
    <PageContainer>
      {!contentLoaded ? (
        <LoadingText>Loading...</LoadingText>
      ) : (
        <div>
          <h2>Day-trading</h2>
          <h3>Top picks for today</h3>
          <ul>
            {data && data['Day Trading']?.map((stock, index) => (
              <li key={index}>
                <div>
                  <TickerText>Ticker: {stock.ticker}</TickerText>
                  <TickerText>Price: ${stock.price ? stock.price.toFixed(2) : 'N/A'}</TickerText>
                  <TickerText>Name: {stock.name}</TickerText>
                  <TickerText>Change Percent: {stock.change_percent ? stock.change_percent.toFixed(2) : 'N/A'}%</TickerText>
                  <TickerText>Volume: {stock.volume ? stock.volume.toLocaleString() : 'N/A'}</TickerText>
                </div>
                <img src={Logo} alt="Logo" />
              </li>
            ))}
          </ul>
        </div>
      )}
    </PageContainer>
  );
}
